import { chromium } from '@playwright/test';
import * as fs from 'fs';
import * as path from 'path';
import * as dotenv from 'dotenv';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Load environment variables from .env.local
dotenv.config({ path: '.env.local' });
dotenv.config({ path: '.env' });

async function globalSetup() {
  const email = process.env.E2E_EMAIL || 'contact@autoscaleai.ca';
  const password = process.env.E2E_PASSWORD || 'Test1234!';
  const supabaseUrl = process.env.VITE_SUPABASE_URL;
  const supabaseKey = process.env.VITE_SUPABASE_PUBLISHABLE_KEY;
  const serviceRoleKey = process.env.SUPABASE_SERVICE_ROLE_KEY;

  if (!supabaseUrl || !supabaseKey) {
    // eslint-disable-next-line no-console
    console.error('Missing Supabase configuration');
    return;
  }

  // eslint-disable-next-line no-console
  console.log('üîê Setting up E2E authentication...');
  // eslint-disable-next-line no-console
  console.log(`üìß Email: ${email}`);
  // eslint-disable-next-line no-console
  console.log(`üåê Supabase URL: ${supabaseUrl}`);

  // First, ensure the test user exists using service role key
  if (serviceRoleKey) {
    try {
      // Check if user exists
      const checkResponse = await fetch(`${supabaseUrl}/auth/v1/admin/users?email=${email}`, {
        headers: {
          'Authorization': `Bearer ${serviceRoleKey}`,
          'apikey': serviceRoleKey,
        }
      });

      if (checkResponse.ok) {
        const data = await checkResponse.json();
        if (!data.users || data.users.length === 0) {
          // eslint-disable-next-line no-console
          console.log('üìù Creating test user...');
          // Create user if doesn't exist
          const createResponse = await fetch(`${supabaseUrl}/auth/v1/admin/users`, {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${serviceRoleKey}`,
              'apikey': serviceRoleKey,
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({
              email,
              password,
              email_confirm: true,
              user_metadata: {
                name: 'E2E Test User',
                role: 'test'
              }
            })
          });

          if (createResponse.ok) {
            // eslint-disable-next-line no-console
            console.log('‚úÖ Test user created successfully');
          } else {
            const error = await createResponse.text();
            // eslint-disable-next-line no-console
            console.log('‚ö†Ô∏è Could not create user:', error);
          }
        } else {
          // eslint-disable-next-line no-console
          console.log('‚úÖ Test user already exists');
        }
      }
    } catch (error) {
      // eslint-disable-next-line no-console
      console.log('‚ö†Ô∏è Could not verify/create user:', error);
    }
  }

  // Launch browser and authenticate
  const browser = await chromium.launch();
  const context = await browser.newContext();
  const page = await context.newPage();

  try {
    // Navigate to the app auth page
    // eslint-disable-next-line no-console
    console.log('üåê Navigating to auth page...');
    await page.goto('http://localhost:4173/auth');

    // Wait for auth page to load
    await page.waitForLoadState('domcontentloaded');
    await page.waitForTimeout(2000); // Allow React to hydrate

    // Updated selectors based on actual AuthNew.tsx structure
    // eslint-disable-next-line no-console
    console.log('üîç Looking for login form...');

    // The form uses react-hook-form with specific placeholders
    const emailInput = page.locator('input[placeholder="Email"]');
    const passwordInput = page.locator('input[placeholder="Mot de passe"]');

    // Wait for form elements to be visible
    await emailInput.waitFor({ state: 'visible', timeout: 10000 });
    await passwordInput.waitFor({ state: 'visible', timeout: 10000 });

    // eslint-disable-next-line no-console
    console.log('üìù Filling login form...');
    await emailInput.fill(email);
    await passwordInput.fill(password);

    // Find the submit button - it shows "Se connecter" in login mode
    const submitButton = page.locator('button[type="submit"]:has-text("Se connecter")');
    await submitButton.waitFor({ state: 'visible', timeout: 5000 });

    // eslint-disable-next-line no-console
    console.log('üöÄ Submitting login form...');
    await submitButton.click();

    // Wait for one of several possible post-login states
    // eslint-disable-next-line no-console
    console.log('‚è≥ Waiting for authentication...');
    try {
      // Wait for navigation to dashboard or any authenticated page
      await page.waitForURL(/\/(dashboard|home|app)/, { timeout: 15000 });
      // eslint-disable-next-line no-console
      console.log('‚úÖ Successfully navigated to authenticated page');
    } catch (_navigationError) {
      // Check if we're still on auth page with success message
      const successAlert = page.locator('.border-green-500, .text-green-600, :has-text("Connexion r√©ussie")');
      const isSuccess = await successAlert.count() > 0;

      if (isSuccess) {
        // eslint-disable-next-line no-console
        console.log('‚úÖ Login success detected, waiting for redirect...');
        await page.waitForTimeout(3000);

        // Try navigating to dashboard manually if auto-redirect failed
        if (page.url().includes('/auth')) {
          // eslint-disable-next-line no-console
          console.log('üîÑ Manually navigating to dashboard...');
          await page.goto('http://localhost:4173/dashboard');
          await page.waitForLoadState('domcontentloaded');
        }
      } else {
        // Check for error messages
        const errorAlert = page.locator('.border-destructive, .text-destructive, :has-text("Erreur")');
        const errorCount = await errorAlert.count();

        if (errorCount > 0) {
          const errorText = await errorAlert.first().textContent();
          // eslint-disable-next-line no-console
          console.log('‚ùå Login error detected:', errorText);
        } else {
          // eslint-disable-next-line no-console
          console.log('‚ö†Ô∏è Navigation timeout but no clear error - continuing...');
        }
      }
    }

    // Wait for the page to stabilize
    await page.waitForTimeout(2000);

    // Save the authenticated state
    const authDir = path.join(__dirname, '.auth');
    if (!fs.existsSync(authDir)) {
      fs.mkdirSync(authDir, { recursive: true });
    }

    await context.storageState({ path: path.join(authDir, 'user.json') });
    // eslint-disable-next-line no-console
    console.log('‚úÖ Authentication state saved to e2e/.auth/user.json');

    // Verify we have a valid session by checking for authenticated content
    const currentUrl = page.url();
    // eslint-disable-next-line no-console
    console.log(`üìç Final URL: ${currentUrl}`);

    if (currentUrl.includes('/dashboard') || currentUrl.includes('/app')) {
      // eslint-disable-next-line no-console
      console.log('üéâ Authentication setup completed successfully!');
    } else {
      // eslint-disable-next-line no-console
      console.log('‚ö†Ô∏è Authentication may not be complete - check session manually');
    }

  } catch (error) {
    console.error('‚ùå Authentication setup failed:', error);

    // Save a screenshot for debugging
    try {
      await page.screenshot({ path: 'e2e-results/auth-setup-error.png', fullPage: true });
      // eslint-disable-next-line no-console
      console.log('üì∏ Error screenshot saved to e2e-results/auth-setup-error.png');
    } catch (_screenshotError) {
      // eslint-disable-next-line no-console
      console.log('‚ö†Ô∏è Could not save error screenshot');
    }

    // Try to save whatever state we have
    try {
      const authDir = path.join(__dirname, '.auth');
      if (!fs.existsSync(authDir)) {
        fs.mkdirSync(authDir, { recursive: true });
      }
      await context.storageState({ path: path.join(authDir, 'user.json') });
      // eslint-disable-next-line no-console
      console.log('‚ö†Ô∏è Partial state saved despite errors');
    } catch (_saveError) {
      // eslint-disable-next-line no-console
      console.log('‚ùå Could not save authentication state');
    }
  } finally {
    await browser.close();
    // eslint-disable-next-line no-console
    console.log('üîí Browser closed');
  }
}

export default globalSetup;

import React, { Component, ErrorInfo, ReactNode } from 'react';
import { AlertTriangle, RefreshCw, Bug, Home } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ErrorHandler } from '@/lib/errors/ErrorHandler';
import { AppError } from '@/lib/errors/AppError';
import { ErrorCategory, ErrorSeverity } from '@/lib/errors/types';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  level?: 'page' | 'component' | 'feature';
  name?: string;
  onError?: (error: AppError, errorInfo: ErrorInfo) => void;
  enableRecovery?: boolean;
}

interface State {
  hasError: boolean;
  error?: AppError;
  errorInfo?: ErrorInfo;
  retryCount: number;
}

class ErrorBoundary extends Component<Props, State> {
  private errorHandler: ErrorHandler;
  private readonly maxRetries = 3;

  constructor(props: Props) {
    super(props);
    this.state = {
      hasError: false,
      retryCount: 0
    };
    this.errorHandler = ErrorHandler.getInstance();
  }

  static getDerivedStateFromError(error: Error): Partial<State> {
    return { hasError: true };
  }

  async componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Convert to AppError for standardized handling
    const appError = new AppError({
      code: 'UNK_002',
      message: error.message,
      category: ErrorCategory.UNKNOWN,
      severity: this.getSeverityFromLevel(),
      source: this.props.name || 'ErrorBoundary',
      details: {
        componentStack: errorInfo.componentStack,
        errorBoundaryLevel: this.props.level || 'component',
        retryCount: this.state.retryCount
      },
      cause: error
    });

    // Handle error through centralized handler
    await this.errorHandler.handleError(error, {
      component: this.props.name || 'ErrorBoundary',
      operation: 'render',
      timestamp: new Date().toISOString(),
      url: window.location.href,
      userAgent: navigator.userAgent,
      additionalData: {
        level: this.props.level,
        componentStack: errorInfo.componentStack
      }
    }, {
      enableUserFeedback: false, // We'll handle it here
      enableRecovery: this.props.enableRecovery
    });

    this.setState({ error: appError, errorInfo });

    // Call custom error handler if provided
    if (this.props.onError) {
      this.props.onError(appError, errorInfo);
    }
  }

  private getSeverityFromLevel(): ErrorSeverity {
    switch (this.props.level) {
      case 'page':
        return ErrorSeverity.HIGH;
      case 'feature':
        return ErrorSeverity.MEDIUM;
      case 'component':
      default:
        return ErrorSeverity.LOW;
    }
  }

  reset = () => {
    this.setState({
      hasError: false,
      error: undefined,
      errorInfo: undefined,
      retryCount: this.state.retryCount + 1
    });
  };

  goHome = () => {
    window.location.href = '/';
  };

  reportError = () => {
    if (this.state.error) {
      // Create error report
      const report = {
        error: this.state.error.toJSON(),
        errorInfo: this.state.errorInfo,
        userAgent: navigator.userAgent,
        url: window.location.href,
        timestamp: new Date().toISOString()
      };

      // Copy to clipboard for user to send
      navigator.clipboard.writeText(JSON.stringify(report, null, 2));

      // Could also open email client or feedback form
      const subject = encodeURIComponent(`Erreur Application: ${this.state.error.code}`);
      const body = encodeURIComponent(`Une erreur s'est produite:\n\nCode: ${this.state.error.code}\nMessage: ${this.state.error.message}\n\nLes d√©tails techniques ont √©t√© copi√©s dans le presse-papiers.`);
      window.open(`mailto:support@example.com?subject=${subject}&body=${body}`);
    }
  };

  render() {
    if (this.state.hasError) {
      // Use custom fallback if provided
      if (this.props.fallback) {
        return this.props.fallback;
      }

      const error = this.state.error;
      const level = this.props.level || 'component';
      const canRetry = this.state.retryCount < this.maxRetries;
      const showDetailedError = process.env.NODE_ENV === 'development';

      // Component-level errors show smaller UI
      if (level === 'component') {
        return (
          <div className="border border-destructive/20 rounded-lg p-4 bg-destructive/5">
            <div className="flex items-center gap-2 mb-2">
              <AlertTriangle className="h-4 w-4 text-destructive" />
              <span className="text-sm font-medium text-destructive">Erreur de Composant</span>
              {error && (
                <Badge variant="outline" className="text-xs">
                  {error.code}
                </Badge>
              )}
            </div>
            <p className="text-sm text-muted-foreground mb-3">
              {error?.userMessage || 'Un composant a rencontr√© une erreur.'}
            </p>

            {showDetailedError && error && (
              <details className="text-xs bg-background p-2 rounded border mb-3">
                <summary className="cursor-pointer font-medium">D√©tails techniques</summary>
                <pre className="mt-2 text-xs overflow-auto">
                  {error.message}
                </pre>
              </details>
            )}

            <div className="flex gap-2">
              {canRetry && (
                <Button size="sm" onClick={this.reset} className="gap-1">
                  <RefreshCw className="h-3 w-3" />
                  R√©essayer
                </Button>
              )}
              <Button size="sm" variant="outline" onClick={() => window.location.reload()}>
                Recharger
              </Button>
            </div>
          </div>
        );
      }

      // Page-level or feature-level errors show full-screen UI
      return (
        <div className="min-h-screen flex items-center justify-center p-4 bg-background">
          <Card className="w-full max-w-lg">
            <CardHeader className="text-center">
              <div className="mx-auto w-16 h-16 bg-destructive/10 rounded-full flex items-center justify-center mb-4">
                <AlertTriangle className="h-8 w-8 text-destructive" />
              </div>
              <CardTitle className="text-destructive flex items-center justify-center gap-2">
                {level === 'page' ? 'Erreur de Page' : 'Erreur de Fonctionnalit√©'}
                {error && (
                  <Badge variant="destructive" className="text-xs">
                    {error.code}
                  </Badge>
                )}
              </CardTitle>
            </CardHeader>

            <CardContent className="space-y-4">
              <div className="text-center">
                <p className="text-muted-foreground">
                  {error?.userMessage || 'Une erreur inattendue s\'est produite.'}
                </p>

                {error?.severity === ErrorSeverity.HIGH && (
                  <p className="text-sm text-destructive mt-2">
                    Cette erreur n√©cessite une attention imm√©diate.
                  </p>
                )}
              </div>

              {showDetailedError && error && (
                <details className="text-left text-sm bg-destructive/5 p-3 rounded border">
                  <summary className="cursor-pointer font-medium mb-2">
                    D√©tails de l'erreur
                  </summary>
                  <div className="space-y-2">
                    <div>
                      <strong>Message:</strong> <code className="text-xs">{error.message}</code>
                    </div>
                    <div>
                      <strong>Source:</strong> <code className="text-xs">{error.source}</code>
                    </div>
                    <div>
                      <strong>Cat√©gorie:</strong> <code className="text-xs">{error.category}</code>
                    </div>
                    <div>
                      <strong>ID:</strong> <code className="text-xs">{error.id}</code>
                    </div>
                    {this.state.errorInfo?.componentStack && (
                      <div>
                        <strong>Stack:</strong>
                        <pre className="text-xs mt-1 overflow-auto max-h-32 bg-background p-2 rounded">
                          {this.state.errorInfo.componentStack}
                        </pre>
                      </div>
                    )}
                  </div>
                </details>
              )}

              <div className="flex flex-col gap-2">
                <div className="flex gap-2 justify-center">
                  {canRetry && (
                    <Button onClick={this.reset} className="gap-2">
                      <RefreshCw className="h-4 w-4" />
                      R√©essayer {this.state.retryCount > 0 && `(${this.state.retryCount}/${this.maxRetries})`}
                    </Button>
                  )}

                  <Button variant="outline" onClick={() => window.location.reload()}>
                    Recharger la page
                  </Button>
                </div>

                <div className="flex gap-2 justify-center">
                  <Button variant="ghost" onClick={this.goHome} className="gap-2">
                    <Home className="h-4 w-4" />
                    Retour √† l'accueil
                  </Button>

                  <Button variant="ghost" onClick={this.reportError} className="gap-2">
                    <Bug className="h-4 w-4" />
                    Signaler l'erreur
                  </Button>
                </div>

                {this.state.retryCount >= this.maxRetries && (
                  <div className="text-center text-sm text-muted-foreground mt-4 p-3 bg-muted rounded">
                    Trop de tentatives √©chou√©es. Veuillez recharger la page ou contacter le support.
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

// Hook for functional components error handling
export const useErrorBoundary = () => {
  const throwError = (error: Error | AppError) => {
    throw error;
  };

  return { throwError };
};

/**
 * Unified API Configuration
 * Consolidates all API configuration patterns into a single, secure source
 * - Environment-based configuration with validation
 * - Feature flags for conditional service enabling
 * - Security-first design with proper secret handling
 * - Type-safe configuration interfaces
 * - Runtime validation and health checks
 */

import { logger } from '@/lib/logger';

// Environment variable validation
interface EnvironmentConfig {
  // Core API
  API_BASE_URL: string;
  NODE_ENV: 'development' | 'production' | 'test';

  // Supabase
  VITE_SUPABASE_URL: string;
  VITE_SUPABASE_ANON_KEY: string;

  // VAPI (Voice AI)
  VITE_VAPI_PUBLIC_KEY?: string;
  VITE_VAPI_ASSISTANT_ID?: string;
  VITE_VAPI_WEBHOOK_URL?: string;

  // Google Maps
  VITE_GOOGLE_MAPS_API_KEY?: string;

  // Twilio
  VITE_TWILIO_ACCOUNT_SID?: string;

  // n8n Automation
  VITE_N8N_BASE_URL?: string;

  // Feature flags
  VITE_ENABLE_VAPI?: string;
  VITE_ENABLE_MAPS?: string;
  VITE_ENABLE_SMS?: string;
  VITE_ENABLE_AUTOMATION?: string;
  VITE_ENABLE_ANALYTICS?: string;
}

// Unified configuration interface
export interface UnifiedAPIConfig {
  // Environment
  environment: 'development' | 'production' | 'test';
  isDevelopment: boolean;
  isProduction: boolean;

  // Core API settings
  api: {
    baseUrl: string;
    timeout: number;
    retries: number;
    enableLogging: boolean;
  };

  // Authentication & Security
  auth: {
    supabaseUrl: string;
    supabaseAnonKey: string;
    enableAutoRefresh: boolean;
    tokenExpiryBuffer: number; // seconds
  };

  // Feature-based service configuration
  services: {
    vapi: {
      enabled: boolean;
      publicKey?: string;
      assistantId?: string;
      webhookUrl?: string;
      apiUrl: string;
    };
    maps: {
      enabled: boolean;
      apiKey?: string;
      apiUrl: string;
    };
    sms: {
      enabled: boolean;
      accountSid?: string;
      apiUrl: string;
    };
    automation: {
      enabled: boolean;
      baseUrl?: string;
      apiUrl: string;
    };
    analytics: {
      enabled: boolean;
      apiUrl: string;
    };
  };

  // Request defaults
  defaults: {
    headers: Record<string, string>;
    timeout: number;
    retries: number;
    cacheTimeout: number;
  };

  // Circuit breaker settings
  circuitBreaker: {
    enabled: boolean;
    failureThreshold: number;
    recoveryTimeout: number;
    monitoringPeriod: number;
  };

  // Rate limiting
  rateLimit: {
    enabled: boolean;
    requestsPerMinute: number;
    burstLimit: number;
  };

  // Caching
  cache: {
    enabled: boolean;
    defaultTtl: number;
    maxSize: number;
  };
}

/**
 * Environment variable loading with validation
 */
function getEnvironmentConfig(): EnvironmentConfig {
  // Get environment variables with proper typing
  const env = {
    API_BASE_URL: getEnvVar('VITE_API_BASE_URL', 'http://localhost:8080'),
    NODE_ENV: (getEnvVar('NODE_ENV', 'development') as 'development' | 'production' | 'test'),

    // Required Supabase config
    VITE_SUPABASE_URL: getEnvVar('VITE_SUPABASE_URL', '', true),
    VITE_SUPABASE_ANON_KEY: getEnvVar('VITE_SUPABASE_ANON_KEY', '', true),

    // Optional service configs
    VITE_VAPI_PUBLIC_KEY: getEnvVar('VITE_VAPI_PUBLIC_KEY'),
    VITE_VAPI_ASSISTANT_ID: getEnvVar('VITE_VAPI_ASSISTANT_ID'),
    VITE_VAPI_WEBHOOK_URL: getEnvVar('VITE_VAPI_WEBHOOK_URL'),

    VITE_GOOGLE_MAPS_API_KEY: getEnvVar('VITE_GOOGLE_MAPS_API_KEY'),

    VITE_TWILIO_ACCOUNT_SID: getEnvVar('VITE_TWILIO_ACCOUNT_SID'),

    VITE_N8N_BASE_URL: getEnvVar('VITE_N8N_BASE_URL'),

    // Feature flags
    VITE_ENABLE_VAPI: getEnvVar('VITE_ENABLE_VAPI', 'true'),
    VITE_ENABLE_MAPS: getEnvVar('VITE_ENABLE_MAPS', 'true'),
    VITE_ENABLE_SMS: getEnvVar('VITE_ENABLE_SMS', 'true'),
    VITE_ENABLE_AUTOMATION: getEnvVar('VITE_ENABLE_AUTOMATION', 'true'),
    VITE_ENABLE_ANALYTICS: getEnvVar('VITE_ENABLE_ANALYTICS', 'true'),
  };

  // Validate required variables
  validateRequiredConfig(env);

  return env;
}

/**
 * Get environment variable with optional validation
 */
function getEnvVar(key: string, defaultValue?: string, required = false): string {
  const value = import.meta.env?.[key] || 
               (globalThis as any)?.process?.env?.[key] || 
               (globalThis as any)?.[key] ||
               defaultValue;

  if (required && !value) {
    throw new Error(`Required environment variable ${key} is not set`);
  }

  return value || '';
}

/**
 * Validate required configuration
 */
function validateRequiredConfig(env: EnvironmentConfig): void {
  const requiredFields: Array<keyof EnvironmentConfig> = [
    'VITE_SUPABASE_URL',
    'VITE_SUPABASE_ANON_KEY',
  ];

  const missing = requiredFields.filter(field => !env[field]);

  if (missing.length > 0) {
    throw new Error(`Missing required environment variables: ${missing.join(', ')}`);
  }

  // Validate URLs
  validateUrl(env.VITE_SUPABASE_URL, 'VITE_SUPABASE_URL');
  if (env.VITE_N8N_BASE_URL) {
    validateUrl(env.VITE_N8N_BASE_URL, 'VITE_N8N_BASE_URL');
  }
}

/**
 * Validate URL format
 */
function validateUrl(url: string, name: string): void {
  try {
    new URL(url);
  } catch {
    throw new Error(`Invalid URL format for ${name}: ${url}`);
  }
}

/**
 * Check if feature is enabled
 */
function isFeatureEnabled(feature: string, env: EnvironmentConfig): boolean {
  const envKey = `VITE_ENABLE_${feature.toUpperCase()}` as keyof EnvironmentConfig;
  const value = env[envKey];
  return value === 'true' || value === '1';
}

/**
 * Create unified configuration from environment
 */
function createUnifiedConfig(): UnifiedAPIConfig {
  const env = getEnvironmentConfig();

  return {
    environment: env.NODE_ENV,
    isDevelopment: env.NODE_ENV === 'development',
    isProduction: env.NODE_ENV === 'production',

    api: {
      baseUrl: env.API_BASE_URL,
      timeout: 30000,
      retries: 3,
      enableLogging: env.NODE_ENV === 'development',
    },

    auth: {
      supabaseUrl: env.VITE_SUPABASE_URL,
      supabaseAnonKey: env.VITE_SUPABASE_ANON_KEY,
      enableAutoRefresh: true,
      tokenExpiryBuffer: 300, // 5 minutes
    },

    services: {
      vapi: {
        enabled: isFeatureEnabled('vapi', env) && Boolean(env.VITE_VAPI_PUBLIC_KEY),
        publicKey: env.VITE_VAPI_PUBLIC_KEY,
        assistantId: env.VITE_VAPI_ASSISTANT_ID,
        webhookUrl: env.VITE_VAPI_WEBHOOK_URL,
        apiUrl: 'https://api.vapi.ai',
      },
      maps: {
        enabled: isFeatureEnabled('maps', env) && Boolean(env.VITE_GOOGLE_MAPS_API_KEY),
        apiKey: env.VITE_GOOGLE_MAPS_API_KEY,
        apiUrl: 'https://maps.googleapis.com/maps/api',
      },
      sms: {
        enabled: isFeatureEnabled('sms', env) && Boolean(env.VITE_TWILIO_ACCOUNT_SID),
        accountSid: env.VITE_TWILIO_ACCOUNT_SID,
        apiUrl: 'https://api.twilio.com',
      },
      automation: {
        enabled: isFeatureEnabled('automation', env) && Boolean(env.VITE_N8N_BASE_URL),
        baseUrl: env.VITE_N8N_BASE_URL,
        apiUrl: env.VITE_N8N_BASE_URL || 'http://localhost:5678',
      },
      analytics: {
        enabled: isFeatureEnabled('analytics', env),
        apiUrl: `${env.API_BASE_URL}/api/analytics`,
      },
    },

    defaults: {
      headers: {
        'Content-Type': 'application/json',
        'X-Requested-With': 'XMLHttpRequest',
        'X-Client-Version': '1.0.0',
      },
      timeout: 30000,
      retries: 3,
      cacheTimeout: 5 * 60 * 1000, // 5 minutes
    },

    circuitBreaker: {
      enabled: true,
      failureThreshold: 5,
      recoveryTimeout: 60000, // 1 minute
      monitoringPeriod: 10000, // 10 seconds
    },

    rateLimit: {
      enabled: true,
      requestsPerMinute: env.NODE_ENV === 'production' ? 60 : 200,
      burstLimit: env.NODE_ENV === 'production' ? 10 : 50,
    },

    cache: {
      enabled: true,
      defaultTtl: 5 * 60 * 1000, // 5 minutes
      maxSize: 100, // 100 entries
    },
  };
}

/**
 * Configuration validation
 */
export function validateConfig(config: UnifiedAPIConfig): {
  valid: boolean;
  errors: string[];
  warnings: string[];
} {
  const errors: string[] = [];
  const warnings: string[] = [];

  // Validate required fields
  if (!config.auth.supabaseUrl) {
    errors.push('Supabase URL is required');
  }

  if (!config.auth.supabaseAnonKey) {
    errors.push('Supabase anonymous key is required');
  }

  // Check service configuration
  if (config.services.vapi.enabled && !config.services.vapi.publicKey) {
    warnings.push('VAPI service enabled but public key not configured');
  }

  if (config.services.maps.enabled && !config.services.maps.apiKey) {
    warnings.push('Maps service enabled but API key not configured');
  }

  if (config.services.sms.enabled && !config.services.sms.accountSid) {
    warnings.push('SMS service enabled but account SID not configured');
  }

  // Validate timeout values
  if (config.api.timeout < 1000) {
    warnings.push('API timeout is very low (<1s)');
  }

  if (config.api.timeout > 60000) {
    warnings.push('API timeout is very high (>60s)');
  }

  return {
    valid: errors.length === 0,
    errors,
    warnings,
  };
}

/**
 * Get service health check endpoints
 */
export function getHealthCheckEndpoints(config: UnifiedAPIConfig): Record<string, string> {
  const endpoints: Record<string, string> = {
    api: `${config.api.baseUrl}/health`,
    supabase: `${config.auth.supabaseUrl}/rest/v1/`,
  };

  if (config.services.vapi.enabled) {
    endpoints.vapi = `${config.services.vapi.apiUrl}/health`;
  }

  if (config.services.automation.enabled && config.services.automation.baseUrl) {
    endpoints.n8n = `${config.services.automation.baseUrl}/healthz`;
  }

  return endpoints;
}

/**
 * Create configuration singleton
 */
let configInstance: UnifiedAPIConfig | null = null;

export function getUnifiedConfig(): UnifiedAPIConfig {
  if (!configInstance) {
    try {
      configInstance = createUnifiedConfig();

      // Validate configuration
      const validation = validateConfig(configInstance);

      if (!validation.valid) {
        logger.error('Configuration validation failed', {
          errors: validation.errors,
        });
        throw new Error(`Configuration errors: ${validation.errors.join(', ')}`);
      }

      if (validation.warnings.length > 0) {
        logger.warn('Configuration warnings', {
          warnings: validation.warnings,
        });
      }

      logger.info('Unified API configuration initialized', {
        environment: configInstance.environment,
        enabledServices: Object.entries(configInstance.services)
          .filter(([, service]) => service.enabled)
          .map(([name]) => name),
      });

    } catch (error) {
      logger.error('Failed to initialize configuration', { error });
      throw error;
    }
  }

  return configInstance;
}

/**
 * Reset configuration (useful for testing)
 */
export function resetConfig(): void {
  configInstance = null;
}

/**
 * Check if a service is properly configured and enabled
 */
export function isServiceEnabled(serviceName: keyof UnifiedAPIConfig['services']): boolean {
  const config = getUnifiedConfig();
  return config.services[serviceName].enabled;
}

/**
 * Get service configuration
 */
export function getServiceConfig<T extends keyof UnifiedAPIConfig['services']>(
  serviceName: T
): UnifiedAPIConfig['services'][T] {
  const config = getUnifiedConfig();
  return config.services[serviceName];
}

/**
 * Export the unified configuration
 */
export const unifiedConfig = getUnifiedConfig();

// Export configuration object for backwards compatibility
export default unifiedConfig;

