interface ExportData {
  period: string;
  analytics: DashboardMetrics | null;
  calls: any[];
  clients: any[];
}

export const useAnalyticsExport = () => {
  const { success, error: showError } = useToast();

  const handleExport = useCallback(async (
    format: 'csv' | 'pdf',
    data: ExportData,
    setIsExporting: (value: boolean) => void
  ) => {
    setIsExporting(true);
    try {
      // Simulate export process with validation
      await new Promise(resolve => setTimeout(resolve, 2000));

      const exportData = {
        period: data.period,
        analytics: data.analytics || {},
        calls: data.calls.slice(0, 100), // Limit for performance
        clients: data.clients.slice(0, 100),
        exportDate: new Date().toISOString(),
        exportFormat: format,
        totalRecords: {
          calls: data.calls.length,
          clients: data.clients.length
        }
      };

      if (format === 'csv') {
        const csvContent = [
          ['Date Export', 'P√©riode', 'Total Appels', 'Appels Actifs', 'Taux Succ√®s', 'Dur√©e Moyenne'],
          [
            new Date().toLocaleDateString('fr-FR'),
            data.period,
            data.analytics?.totalCalls || data.calls.length,
            data.analytics?.activeCalls || data.calls.filter(c => c.status === 'active').length,
            `${data.analytics?.successRate || 0}%`,
            `${data.analytics?.avgResponseTime || 0}min`
          ]
        ].map(row =>
          row.map(cell => `"${String(cell).replace(/"/g, '""')}"`).join(',')
        ).join('\n');

        const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `drain-fortin-analytics-${data.period}-${new Date().toISOString().split('T')[0]}.csv`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        URL.revokeObjectURL(url);
      }

      success("Export r√©ussi", `Rapport ${format.toUpperCase()} g√©n√©r√© avec succ√®s`);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Erreur d\'export';
      showError("Erreur d'export", `Impossible de g√©n√©rer le rapport: ${errorMessage}`);
    } finally {
      setIsExporting(false);
    }
  }, [success, showError]);

  return { handleExport };
};

import { useState, useMemo } from "react";

interface FilterConfig<T> {
  searchFields: (keyof T)[];
  defaultFilter?: string;
}

export function useFilters<T extends Record<string, any>>(
  items: T[],
  config: FilterConfig<T>
) {
  const [searchTerm, setSearchTerm] = useState("");
  const [statusFilter, setStatusFilter] = useState(config.defaultFilter || "tous");

  const filteredItems = useMemo(() => {
    return items.filter(item => {
      // Search filter
      const matchesSearch = config.searchFields.some(field => {
        const value = item[field];
        if (typeof value === 'string') {
          return value.toLowerCase().includes(searchTerm.toLowerCase());
        }
        if (typeof value === 'number') {
          return value.toString().includes(searchTerm);
        }
        return false;
      });

      // Status filter
      const matchesStatus = statusFilter === "tous" || item.status === statusFilter;

      return matchesSearch && matchesStatus;
    });
  }, [items, searchTerm, statusFilter, config.searchFields]);

  return {
    searchTerm,
    setSearchTerm,
    statusFilter,
    setStatusFilter,
    filteredItems
  };
}

