  useEffect(() => {
    const subscription = supabaseServices.subscribeToSMSLogs((payload: SupabaseRealtimePayload<SMSLog>) => {
      queryClient.invalidateQueries({ queryKey: ['supabase', 'sms_logs'] });
    });

    return () => {
      supabaseServices.unsubscribeFromTable(subscription);
    };
  }, [queryClient]);

  return query;
}

