};

/**
 * ULTRA-OPTIMIZED WEBSOCKET HOOK
 * Real-time updates with automatic reconnection and event handling
 */

import { useEffect, useRef, useCallback, useState } from 'react';
import { toast } from 'sonner';
import { logger } from '@/lib/logger';
import type {
  VAPIWebSocketMessage,
  AlertWebSocketMessage
} from '@/types/api.types';

export interface WebSocketMessage {
  type: string;
  data?: {
    phoneNumber?: string;
    duration?: number;
    callId?: string;
    transcription?: string;
    severity?: 'low' | 'medium' | 'high' | 'critical';
    message?: string;
    [key: string]: unknown;
  };
  timestamp?: string;
}

interface UseWebSocketOptions {
  url?: string;
  reconnectInterval?: number;
  maxReconnectAttempts?: number;
  onMessage?: (message: WebSocketMessage) => void;
  onConnect?: () => void;
  onDisconnect?: () => void;
  onError?: (error: Event) => void;
}

export function useWebSocket({
  url,
  reconnectInterval = 3000,
  maxReconnectAttempts = 5,
  onMessage,
  onConnect,
  onDisconnect,
  onError
}: UseWebSocketOptions = {}) {
  const [isConnected, setIsConnected] = useState(false);
  const [reconnectCount, setReconnectCount] = useState(0);
  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout>();
  const pingIntervalRef = useRef<NodeJS.Timeout>();

  // Get WebSocket URL
  const getWebSocketUrl = useCallback(() => {
    if (url) return url;
    const envUrl = import.meta.env?.VITE_WS_URL as string | undefined;
    if (envUrl && typeof envUrl === 'string') return envUrl;
    const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
    const host = window.location.host;
    return `${protocol}//${host}/ws`;
  }, [url]);

  // Connect to WebSocket
  const connect = useCallback(() => {
    try {
      const wsUrl = getWebSocketUrl();

      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;

      ws.onopen = () => {
        setIsConnected(true);
        setReconnectCount(0);
        onConnect?.();

        // Subscribe to VAPI events
        ws.send(JSON.stringify({
          type: 'subscribe',
          channel: 'vapi-events'
        }));

        // Start ping interval to keep connection alive
        pingIntervalRef.current = setInterval(() => {
          if (ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify({ type: 'ping' }));
          }
        }, 30000); // Ping every 30 seconds
      };

      ws.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data) as WebSocketMessage;

          // Handle different message types
          switch (message.type) {
            case 'connected':
              break;

            case 'pong':
              // Ping response, connection is alive
              break;

            case 'vapi-event':
              handleVapiEvent(message.data);
              break;

            case 'call-started':
              toast.info(`New call started: ${message.data?.phoneNumber}`);
              break;

            case 'call-ended':
              toast.success(`Call completed (${message.data?.duration}s)`);
              break;

            case 'speech-update':
              // Real-time transcription update
              break;

            case 'handoff-triggered':
              toast.warning('Call requires human intervention!', {
                duration: 10000,
                important: true
              });
              break;

            case 'alert':
              handleAlert(message.data);
              break;

            default:
              onMessage?.(message);
          }
        } catch (error) {
          logger.error('Failed to parse WebSocket message:', error);
        }
      };

      ws.onerror = (error) => {
        logger.error('WebSocket error:', error);
        onError?.(error);
      };

      ws.onclose = () => {
        setIsConnected(false);
        onDisconnect?.();

        // Clear ping interval
        if (pingIntervalRef.current) {
          clearInterval(pingIntervalRef.current);
        }

        // Attempt reconnection
        if (reconnectCount < maxReconnectAttempts) {
          const nextReconnectCount = reconnectCount + 1;
          setReconnectCount(nextReconnectCount);


          reconnectTimeoutRef.current = setTimeout(() => {
            connect();
          }, reconnectInterval);
        } else {
          toast.error('WebSocket connection lost. Please refresh the page.');
        }
      };

    } catch (error) {
      logger.error('Failed to establish WebSocket connection:', error);
      onError?.(error as Event);
    }
  }, [getWebSocketUrl, onConnect, onDisconnect, onError, onMessage, reconnectCount, reconnectInterval, maxReconnectAttempts]);

  // Disconnect from WebSocket
  const disconnect = useCallback(() => {
    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
    }

    if (pingIntervalRef.current) {
      clearInterval(pingIntervalRef.current);
    }

    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    setIsConnected(false);
    setReconnectCount(0);
  }, []);

  // Send message through WebSocket
  const sendMessage = useCallback((message: {
    type: string;
    channel?: string;
    data?: Record<string, unknown>;
    [key: string]: unknown;
  }) => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
      return true;
    }
    logger.warn('WebSocket not connected, message not sent:', message);
    return false;
  }, []);

  // Handle VAPI events
  const handleVapiEvent = (data: VAPIWebSocketMessage['data']) => {

    // Update UI based on event type
    switch (data.type) {
      case 'assistant-request':
        // Assistant is processing
        break;
      case 'assistant-response':
        // Assistant responded
        break;
      case 'tool-call':
        // Tool was executed
        break;
      default:
        // Generic VAPI event
        break;
    }

    // Pass to parent handler
    onMessage?.({
      type: 'vapi-event',
      data,
      timestamp: new Date().toISOString()
    });
  };

  // Handle alerts
  const handleAlert = (alert: AlertWebSocketMessage) => {
    const severity = alert.severity || 'info';
    const message = alert.message || 'System alert';

    switch (severity) {
      case 'high':
      case 'critical':
        toast.error(message, { duration: 10000, important: true });
        break;
      case 'medium':
        toast.warning(message, { duration: 7000 });
        break;
      default:
        toast.info(message);
    }
  };

  // Setup and cleanup
  useEffect(() => {
    connect();

    return () => {
      disconnect();
    };
  }, []); // Empty deps, only run once

  // Public API
  return {
    isConnected,
    reconnectCount,
    sendMessage,
    connect,
    disconnect
  };
}

/**
 * AppError Class - Standardized error implementation
 * Extends the native Error class with additional metadata and functionality
 */

import {
  AppError as IAppError,
  ErrorCategory,
  ErrorSeverity,
  ErrorCode,
  ErrorContext,
  UserMessages
} from './types';

export class AppError extends Error implements IAppError {
  readonly id: string;
  readonly code: ErrorCode;
  readonly category: ErrorCategory;
  readonly severity: ErrorSeverity;
  readonly userMessage: string;
  readonly context?: Record<string, unknown>;
  readonly timestamp: string;
  readonly source: string;
  readonly correlationId?: string;
  readonly retryable: boolean;
  readonly retryAfter?: number;
  readonly details?: Record<string, unknown>;

  constructor(options: {
    code: ErrorCode;
    message: string;
    category: ErrorCategory;
    severity: ErrorSeverity;
    source: string;
    userMessage?: string;
    context?: Record<string, unknown>;
    correlationId?: string;
    retryable?: boolean;
    retryAfter?: number;
    details?: Record<string, unknown>;
    cause?: Error;
  }) {
    super(options.message);

    this.name = 'AppError';
    this.id = crypto.randomUUID();
    this.code = options.code;
    this.category = options.category;
    this.severity = options.severity;
    this.source = options.source;
    this.timestamp = new Date().toISOString();
    this.context = options.context;
    this.correlationId = options.correlationId;
    this.retryable = options.retryable ?? this.getDefaultRetryable();
    this.retryAfter = options.retryAfter;
    this.details = options.details;

    // Set user message with fallback
    this.userMessage = options.userMessage ||
                      UserMessages[options.code] ||
                      'Une erreur inattendue s\'est produite.';

    // Preserve the original error stack if provided
    if (options.cause) {
      this.stack = options.cause.stack;
      this.cause = options.cause;
    }

    // Ensure stack trace is captured
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, AppError);
    }
  }

  /**
   * Determine default retryable status based on category
   */
  private getDefaultRetryable(): boolean {
    switch (this.category) {
      case ErrorCategory.NETWORK:
      case ErrorCategory.TIMEOUT:
      case ErrorCategory.SERVER:
      case ErrorCategory.EXTERNAL_SERVICE:
      case ErrorCategory.DATABASE:
        return true;

      case ErrorCategory.RATE_LIMIT:
        return true; // But with delay

      case ErrorCategory.VALIDATION:
      case ErrorCategory.AUTHENTICATION:
      case ErrorCategory.AUTHORIZATION:
      case ErrorCategory.NOT_FOUND:
      case ErrorCategory.BUSINESS_LOGIC:
      case ErrorCategory.CONFIGURATION:
        return false;

      default:
        return false;
    }
  }

  /**
   * Convert error to JSON for serialization
   */
  toJSON(): Record<string, unknown> {
    return {
      id: this.id,
      name: this.name,
      code: this.code,
      message: this.message,
      userMessage: this.userMessage,
      category: this.category,
      severity: this.severity,
      source: this.source,
      timestamp: this.timestamp,
      correlationId: this.correlationId,
      retryable: this.retryable,
      retryAfter: this.retryAfter,
      context: this.context,
      details: this.details,
      stack: this.stack
    };
  }

  /**
   * Create a sanitized version for API responses (no stack trace)
   */
  toSafeJSON(): Record<string, unknown> {
    const json = this.toJSON();
    delete json.stack;
    delete json.details; // May contain sensitive info
    return json;
  }

  /**
   * Check if this error should be retried
   */
  shouldRetry(currentAttempt: number, maxAttempts: number): boolean {
    return this.retryable && currentAttempt < maxAttempts;
  }

  /**
   * Get retry delay in milliseconds
   */
  getRetryDelay(attempt: number, baseDelay = 1000): number {
    if (this.retryAfter) {
      return this.retryAfter;
    }

    // Exponential backoff with jitter
    const delay = baseDelay * Math.pow(2, attempt - 1);
    const jitter = Math.random() * 0.1 * delay;
    return Math.min(delay + jitter, 30000); // Max 30 seconds
  }

  /**
   * Create AppError from native Error
   */
  static fromError(
    error: Error,
    source: string,
    category?: ErrorCategory,
    code?: ErrorCode
  ): AppError {
    return new AppError({
      code: code || 'UNK_001',
      message: error.message,
      category: category || ErrorCategory.UNKNOWN,
      severity: ErrorSeverity.MEDIUM,
      source,
      cause: error,
      context: {
        originalErrorName: error.name,
        originalErrorStack: error.stack
      }
    });
  }

  /**
   * Create AppError from HTTP response
   */
  static fromHttpError(
    status: number,
    statusText: string,
    data: unknown,
    source: string
  ): AppError {
    const category = AppError.categorizeHttpStatus(status);
    const severity = AppError.getSeverityFromStatus(status);
    const code = AppError.getCodeFromStatus(status);

    return new AppError({
      code,
      message: `HTTP ${status}: ${statusText}`,
      category,
      severity,
      source,
      retryable: AppError.isHttpStatusRetryable(status),
      details: {
        status,
        statusText,
        response: data
      }
    });
  }

  /**
   * Create validation error
   */
  static validation(
    field: string,
    value: unknown,
    rule: string,
    source: string
  ): AppError {
    return new AppError({
      code: 'VAL_002',
      message: `Validation failed for ${field}: ${rule}`,
      category: ErrorCategory.VALIDATION,
      severity: ErrorSeverity.LOW,
      source,
      userMessage: `Le champ ${field} n'est pas valide.`,
      details: { field, value, rule }
    });
  }

  /**
   * Create business logic error
   */
  static businessLogic(
    operation: string,
    reason: string,
    source: string,
    code: ErrorCode = 'BIZ_002'
  ): AppError {
    return new AppError({
      code,
      message: `Business rule violation: ${operation} - ${reason}`,
      category: ErrorCategory.BUSINESS_LOGIC,
      severity: ErrorSeverity.MEDIUM,
      source,
      details: { operation, reason }
    });
  }

  /**
   * Helper methods for HTTP error categorization
   */
  private static categorizeHttpStatus(status: number): ErrorCategory {
    if (status === 401) return ErrorCategory.AUTHENTICATION;
    if (status === 403) return ErrorCategory.AUTHORIZATION;
    if (status === 404) return ErrorCategory.NOT_FOUND;
    if (status === 422) return ErrorCategory.VALIDATION;
    if (status === 429) return ErrorCategory.RATE_LIMIT;
    if (status >= 400 && status < 500) return ErrorCategory.VALIDATION;
    if (status >= 500) return ErrorCategory.SERVER;
    return ErrorCategory.UNKNOWN;
  }

  private static getSeverityFromStatus(status: number): ErrorSeverity {
    if (status === 401 || status === 403) return ErrorSeverity.MEDIUM;
    if (status === 404) return ErrorSeverity.LOW;
    if (status === 422) return ErrorSeverity.LOW;
    if (status === 429) return ErrorSeverity.MEDIUM;
    if (status >= 500) return ErrorSeverity.HIGH;
    return ErrorSeverity.MEDIUM;
  }

  private static getCodeFromStatus(status: number): ErrorCode {
    switch (status) {
      case 401: return 'AUTH_001';
      case 403: return 'AUTH_003';
      case 404: return 'VAL_001';
      case 422: return 'VAL_002';
      case 429: return 'NET_004';
      case 500: return 'UNK_002';
      case 502:
      case 503:
      case 504: return 'NET_003';
      default: return 'UNK_001';
    }
  }

  private static isHttpStatusRetryable(status: number): boolean {
    // Retry on server errors and specific client errors
    const retryableStatuses = [408, 429, 500, 502, 503, 504];
    return retryableStatuses.includes(status);
  }
}

/**
 * Enhanced ErrorHandler - Centralized error handling with recovery and monitoring
 * Replaces the existing error handler with improved functionality
 */

import { AppError } from './AppError';
import {
  ErrorCategory,
  ErrorSeverity,
  ErrorContext,
  ErrorHandlerConfig,
  ErrorMonitoringData,
  RecoveryStrategy,
  ErrorRecoveryOptions,
  ErrorFeedbackConfig,
  ErrorFeedbackType
} from './types';
import { logger } from '../logger';

export class ErrorHandler {
  private static instance: ErrorHandler;
  private config: ErrorHandlerConfig;
  private errorCounts = new Map<string, { count: number; lastReset: number }>();
  private recoveryStrategies = new Map<string, ErrorRecoveryOptions>();

  private constructor(config: ErrorHandlerConfig) {
    this.config = config;
    this.setupDefaultRecoveryStrategies();
  }

  static getInstance(config?: Partial<ErrorHandlerConfig>): ErrorHandler {
    if (!ErrorHandler.instance) {
      const defaultConfig = ErrorHandler.getDefaultConfig();
      ErrorHandler.instance = new ErrorHandler({ ...defaultConfig, ...config });
    }
    return ErrorHandler.instance;
  }

  /**
   * Main error handling entry point
   */
  async handleError(
    error: unknown,
    context: ErrorContext,
    options?: {
      enableRecovery?: boolean;
      enableUserFeedback?: boolean;
      customRecovery?: ErrorRecoveryOptions;
    }
  ): Promise<AppError> {
    // Convert to AppError if needed
    const appError = this.toAppError(error, context);

    // Log the error
    this.logError(appError, context);

    // Track error patterns
    this.trackErrorPattern(appError);

    // Send to monitoring if configured
    await this.sendToMonitoring(appError, context);

    // Send critical error notifications
    if (appError.severity === ErrorSeverity.CRITICAL) {
      await this.sendCriticalNotification(appError, context);
    }

    // Attempt recovery if enabled
    if (options?.enableRecovery !== false && this.config.enableRecovery) {
      const recovery = options?.customRecovery || this.getRecoveryStrategy(appError);
      if (recovery.strategy !== RecoveryStrategy.NONE) {
        await this.attemptRecovery(appError, recovery);
      }
    }

    // Show user feedback if enabled
    if (options?.enableUserFeedback !== false && this.config.enableUserFeedback) {
      await this.showUserFeedback(appError);
    }

    return appError;
  }

  /**
   * Handle errors with automatic retry logic
   */
  async executeWithRetry<T>(
    operation: () => Promise<T>,
    context: ErrorContext,
    options?: {
      maxRetries?: number;
      retryDelay?: number;
      retryableCategories?: ErrorCategory[];
    }
  ): Promise<T> {
    const maxRetries = options?.maxRetries ?? this.config.maxRetries;
    const baseDelay = options?.retryDelay ?? this.config.retryDelay;
    const retryableCategories = options?.retryableCategories ?? [
      ErrorCategory.NETWORK,
      ErrorCategory.TIMEOUT,
      ErrorCategory.SERVER,
      ErrorCategory.EXTERNAL_SERVICE
    ];

    let lastError: AppError;
    let attempt = 1;

    while (attempt <= maxRetries) {
      try {
        return await operation();
      } catch (error) {
        lastError = await this.handleError(error, {
          ...context,
          additionalData: { attempt, maxRetries }
        }, { enableUserFeedback: false }); // Don't show feedback during retries

        // Check if we should retry
        if (
          attempt === maxRetries ||
          !lastError.retryable ||
          !retryableCategories.includes(lastError.category)
        ) {
          break;
        }

        // Calculate delay and wait
        const delay = lastError.getRetryDelay(attempt, baseDelay);
        logger.info('Retrying operation', {
          source: context.component || 'unknown',
          operation: context.operation,
          attempt,
          maxRetries,
          delay,
          error: lastError.code
        });

        await this.sleep(delay);
        attempt++;
      }
    }

    throw lastError!;
  }

  /**
   * Convert any error to AppError
   */
  private toAppError(error: unknown, context: ErrorContext): AppError {
    if (error instanceof AppError) {
      return error;
    }

    if (error instanceof Error) {
      return AppError.fromError(error, context.component || 'unknown');
    }

    // Handle fetch/HTTP errors
    if (error && typeof error === 'object' && 'status' in error) {
      const httpError = error as { status: number; statusText?: string; data?: unknown };
      return AppError.fromHttpError(
        httpError.status,
        httpError.statusText || 'Unknown',
        httpError.data,
        context.component || 'unknown'
      );
    }

    // Fallback for unknown error types
    return new AppError({
      code: 'UNK_001',
      message: String(error),
      category: ErrorCategory.UNKNOWN,
      severity: ErrorSeverity.MEDIUM,
      source: context.component || 'unknown',
      context: { originalError: error }
    });
  }

  /**
   * Log error based on severity
   */
  private logError(error: AppError, context: ErrorContext): void {
    const logData = {
      errorId: error.id,
      code: error.code,
      category: error.category,
      severity: error.severity,
      source: error.source,
      component: context.component,
      operation: context.operation,
      userId: context.userId,
      correlationId: error.correlationId,
      message: error.message,
      userMessage: error.userMessage,
      retryable: error.retryable,
      url: context.url,
      userAgent: context.userAgent
    };

    switch (error.severity) {
      case ErrorSeverity.CRITICAL:
        logger.error('CRITICAL ERROR', logData);
        break;
      case ErrorSeverity.HIGH:
        logger.error('High severity error', logData);
        break;
      case ErrorSeverity.MEDIUM:
        logger.warn('Medium severity error', logData);
        break;
      case ErrorSeverity.LOW:
        if (this.config.logLevel === 'debug') {
          logger.debug('Low severity error', logData);
        }
        break;
    }
  }

  /**
   * Track error patterns for analysis
   */
  private trackErrorPattern(error: AppError): void {
    const key = `${error.source}:${error.code}`;
    const now = Date.now();
    const hourMs = 60 * 60 * 1000;

    let errorCount = this.errorCounts.get(key);

    if (!errorCount || (now - errorCount.lastReset) > hourMs) {
      errorCount = { count: 0, lastReset: now };
      this.errorCounts.set(key, errorCount);
    }

    errorCount.count++;

    // Alert on error patterns
    if (errorCount.count > 10) {
      logger.warn('High error rate detected', {
        source: error.source,
        code: error.code,
        count: errorCount.count,
        timeWindow: '1 hour'
      });
    }
  }

  /**
   * Send error to monitoring service
   */
  private async sendToMonitoring(error: AppError, context: ErrorContext): Promise<void> {
    if (!this.config.monitoringEndpoint) {
      return;
    }

    try {
      const monitoringData: ErrorMonitoringData = {
        error,
        context,
        timestamp: new Date().toISOString(),
        environment: process.env.NODE_ENV || 'unknown',
        version: process.env.VITE_APP_VERSION || 'unknown',
        sessionId: context.sessionId,
        userId: context.userId
      };

      await fetch(this.config.monitoringEndpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(monitoringData)
      });
    } catch (monitoringError) {
      logger.error('Failed to send error to monitoring', {
        originalError: error.id,
        monitoringError: monitoringError instanceof Error ?
          monitoringError.message : String(monitoringError)
      });
    }
  }

  /**
   * Send critical error notifications
   */
  private async sendCriticalNotification(error: AppError, context: ErrorContext): Promise<void> {
    if (!this.config.notificationWebhook) {
      return;
    }

    try {
      const payload = {
        type: 'critical_error',
        error: error.toJSON(),
        context,
        timestamp: new Date().toISOString()
      };

      await fetch(this.config.notificationWebhook, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
    } catch (notificationError) {
      logger.error('Failed to send critical error notification', {
        originalError: error.id,
        notificationError: notificationError instanceof Error ?
          notificationError.message : String(notificationError)
      });
    }
  }

  /**
   * Setup default recovery strategies
   */
  private setupDefaultRecoveryStrategies(): void {
    // Network errors - retry
    this.recoveryStrategies.set('NET_001', {
      strategy: RecoveryStrategy.RETRY,
      maxRetries: 3
    });

    // Timeout errors - retry with backoff
    this.recoveryStrategies.set('NET_002', {
      strategy: RecoveryStrategy.RETRY,
      maxRetries: 2,
      retryDelay: 2000
    });

    // Service unavailable - fallback
    this.recoveryStrategies.set('NET_003', {
      strategy: RecoveryStrategy.FALLBACK,
      fallbackAction: () => {
        // Could show cached data or offline mode
        console.log('Using fallback for service unavailable');
      }
    });

    // Authentication errors - redirect to login
    this.recoveryStrategies.set('AUTH_001', {
      strategy: RecoveryStrategy.REDIRECT,
      redirectUrl: '/auth/login'
    });

    // Token expired - refresh
    this.recoveryStrategies.set('AUTH_002', {
      strategy: RecoveryStrategy.REFRESH,
      refreshTarget: 'auth-token'
    });
  }

  /**
   * Get recovery strategy for error
   */
  private getRecoveryStrategy(error: AppError): ErrorRecoveryOptions {
    const strategy = this.recoveryStrategies.get(error.code);
    if (strategy) {
      return strategy;
    }

    // Default strategies based on category
    switch (error.category) {
      case ErrorCategory.NETWORK:
      case ErrorCategory.TIMEOUT:
        return { strategy: RecoveryStrategy.RETRY, maxRetries: 2 };

      case ErrorCategory.AUTHENTICATION:
        return {
          strategy: RecoveryStrategy.REDIRECT,
          redirectUrl: '/auth/login'
        };

      default:
        return { strategy: RecoveryStrategy.NONE };
    }
  }

  /**
   * Attempt error recovery
   */
  private async attemptRecovery(error: AppError, recovery: ErrorRecoveryOptions): Promise<void> {
    try {
      switch (recovery.strategy) {
        case RecoveryStrategy.RETRY:
          // Retry is handled in executeWithRetry method
          break;

        case RecoveryStrategy.FALLBACK:
          if (recovery.fallbackAction) {
            await recovery.fallbackAction();
          }
          break;

        case RecoveryStrategy.REFRESH:
          if (recovery.refreshTarget === 'auth-token') {
            // Trigger token refresh
            window.dispatchEvent(new CustomEvent('auth:refresh-token'));
          } else {
            window.location.reload();
          }
          break;

        case RecoveryStrategy.REDIRECT:
          if (recovery.redirectUrl) {
            window.location.href = recovery.redirectUrl;
          }
          break;

        case RecoveryStrategy.MANUAL:
          if (recovery.customAction) {
            await recovery.customAction.action();
          }
          break;
      }
    } catch (recoveryError) {
      logger.error('Error recovery failed', {
        originalError: error.id,
        recoveryStrategy: recovery.strategy,
        recoveryError: recoveryError instanceof Error ?
          recoveryError.message : String(recoveryError)
      });
    }
  }

  /**
   * Show user feedback for error
   */
  private async showUserFeedback(error: AppError): Promise<void> {
    const feedbackConfig = this.getFeedbackConfig(error);

    // Dispatch custom event for UI components to handle
    window.dispatchEvent(new CustomEvent('app:error-feedback', {
      detail: { error, config: feedbackConfig }
    }));
  }

  /**
   * Get feedback configuration for error
   */
  private getFeedbackConfig(error: AppError): ErrorFeedbackConfig {
    const baseConfig: ErrorFeedbackConfig = {
      type: ErrorFeedbackType.TOAST,
      message: error.userMessage,
      dismissible: true
    };

    // Customize based on severity
    switch (error.severity) {
      case ErrorSeverity.CRITICAL:
        return {
          ...baseConfig,
          type: ErrorFeedbackType.MODAL,
          title: 'Erreur Critique',
          dismissible: false,
          actions: [{
            label: 'Recharger la page',
            action: () => window.location.reload(),
            variant: 'primary'
          }]
        };

      case ErrorSeverity.HIGH:
        return {
          ...baseConfig,
          type: ErrorFeedbackType.BANNER,
          title: 'Erreur Importante',
          duration: 0, // Don't auto-dismiss
          actions: [{
            label: 'R√©essayer',
            action: () => window.location.reload(),
            variant: 'secondary'
          }]
        };

      case ErrorSeverity.MEDIUM:
        return {
          ...baseConfig,
          type: ErrorFeedbackType.TOAST,
          duration: 5000
        };

      case ErrorSeverity.LOW:
        return {
          ...baseConfig,
          type: ErrorFeedbackType.TOAST,
          duration: 3000
        };

      default:
        return baseConfig;
    }
  }

  /**
   * Get error statistics
   */
  getErrorStats(): {
    totalErrors: number;
    errorsByCategory: Record<string, number>;
    errorsBySource: Record<string, number>;
    topErrors: Array<{ key: string; count: number }>;
  } {
    const errorsByCategory: Record<string, number> = {};
    const errorsBySource: Record<string, number> = {};
    const topErrors: Array<{ key: string; count: number }> = [];

    let totalErrors = 0;

    for (const [key, errorCount] of this.errorCounts) {
      totalErrors += errorCount.count;

      const [source, code] = key.split(':');
      errorsBySource[source] = (errorsBySource[source] || 0) + errorCount.count;

      topErrors.push({ key, count: errorCount.count });
    }

    topErrors.sort((a, b) => b.count - a.count);

    return {
      totalErrors,
      errorsByCategory,
      errorsBySource,
      topErrors: topErrors.slice(0, 10)
    };
  }

  /**
   * Utility methods
   */
  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  private static getDefaultConfig(): ErrorHandlerConfig {
    return {
      enableStackTrace: process.env.NODE_ENV === 'development',
      enableUserFeedback: true,
      logLevel: 'error',
      maxRetries: 3,
      retryDelay: 1000,
      enableRecovery: true
    };
  }
}

type LogLevel = 'debug' | 'info' | 'warn' | 'error';

interface LogContext {
  [key: string]: unknown;
}

class Logger {
  private isDevelopment = process.env.NODE_ENV === 'development';

  private log(level: LogLevel, message: string, context?: LogContext): void {
    const timestamp = new Date().toISOString();
    const logEntry = {
      timestamp,
      level,
      message,
      ...context
    };

    if (this.isDevelopment) {
      switch (level) {
        case 'debug':
          console.debug(`[${timestamp}] DEBUG:`, message, context || '');
          break;
        case 'info':
          console.info(`[${timestamp}] INFO:`, message, context || '');
          break;
        case 'warn':
          console.warn(`[${timestamp}] WARN:`, message, context || '');
          break;
        case 'error':
          console.error(`[${timestamp}] ERROR:`, message, context || '');
          break;
      }
    } else {
      // In production, send to logging service
      // This could be replaced with your preferred logging service
      if (level === 'error' || level === 'warn') {
        console.error(JSON.stringify(logEntry));
      }
    }
  }

  debug(message: string, context?: LogContext): void {
    this.log('debug', message, context);
  }

  info(message: string, context?: LogContext): void {
    this.log('info', message, context);
  }

  warn(message: string, context?: LogContext): void {
    this.log('warn', message, context);
  }

  error(message: string, context?: LogContext | Error): void {
    if (context instanceof Error) {
      this.log('error', message, {
        error: context.message,
        stack: context.stack,
      });
    } else {
      this.log('error', message, context);
    }
  }
}

export const logger = new Logger();
export default logger;

