/**
 * API Gateway - Centralized request routing and middleware orchestration
 * Implements enterprise-grade API gateway pattern with:
 * - Request routing and aggregation
 * - Rate limiting and throttling
 * - Authentication/authorization checks
 * - Request/response transformation
 * - Comprehensive logging and monitoring
 */


import { logger } from '@/lib/logger';
import { csrfProtection } from '@/services/security/csrf';
import { withSecurityHeaders } from '@/services/security/headers';
import { RateLimiter } from './RateLimiter';
import { RequestValidator } from './RequestValidator';
import { ResponseTransformer } from './ResponseTransformer';
import { MetricsCollector } from './MetricsCollector';
import { CircuitBreaker } from './CircuitBreaker';

export interface GatewayConfig {
  baseUrl: string;
  timeout: number;
  retries: number;
  rateLimiting: {
    enabled: boolean;
    windowMs: number;
    maxRequests: number;
    skipSuccessful?: boolean;
  };
  auth: {
    required: boolean;
    skipRoutes: string[];
  };
  monitoring: {
    enabled: boolean;
    sampleRate: number;
  };
}

export interface GatewayRequest<T = unknown> {
  method: 'GET' | 'POST' | 'PUT' | 'DELETE' | 'PATCH';
  endpoint: string;
  data?: T;
  headers?: Record<string, string>;
  timeout?: number;
  retries?: number;
  skipAuth?: boolean;
  skipRateLimit?: boolean;
  transform?: (data: unknown) => unknown;
}

export interface GatewayResponse<T = unknown> {
  success: boolean;
  data?: T;
  error?: {
    code: string;
    message: string;
    details?: unknown;
    stack?: string;
  };
  meta: {
    requestId: string;
    timestamp: string;
    duration: number;
    endpoint: string;
    cached?: boolean;
    retryAttempt?: number;
  };
}

export class ApiGateway {
  private static instance: ApiGateway;
  private config: GatewayConfig;
  private rateLimiter: RateLimiter;
  private validator: RequestValidator;
  private transformer: ResponseTransformer;
  private metrics: MetricsCollector;
  private circuitBreakers: Map<string, CircuitBreaker>;

  private constructor(config: GatewayConfig) {
    this.config = config;
    this.rateLimiter = new RateLimiter(config.rateLimiting);
    this.validator = new RequestValidator();
    this.transformer = new ResponseTransformer();
    this.metrics = new MetricsCollector();
    this.circuitBreakers = new Map();
  }

  static getInstance(config?: GatewayConfig): ApiGateway {
    if (!ApiGateway.instance) {
      if (!config) {
        throw new Error('ApiGateway config required for first initialization');
      }
      ApiGateway.instance = new ApiGateway(config);
    }
    return ApiGateway.instance;
  }

  /**
   * Main gateway request handler - orchestrates all middleware
   */
  async request<TRequest = unknown, TResponse = unknown>(
    request: GatewayRequest<TRequest>
  ): Promise<GatewayResponse<TResponse>> {
    const requestId = crypto.randomUUID();
    const startTime = Date.now();

    try {
      // 1. Request validation and preprocessing
      await this.validateRequest(request, requestId);

      // 2. Authentication check
      if (!request.skipAuth && this.config.auth.required) {
        await this.authenticateRequest(request, requestId);
      }

      // 3. Rate limiting
      if (!request.skipRateLimit && this.config.rateLimiting.enabled) {
        await this.checkRateLimit(request, requestId);
      }

      // 4. Circuit breaker check
      const circuitBreaker = this.getCircuitBreaker(request.endpoint);
      if (circuitBreaker.isOpen()) {
        throw new Error('Circuit breaker is open for ' + request.endpoint);
      }

      // 5. Execute request with retries
      const response = await this.executeWithRetries(request, requestId);

      // 6. Transform response
      const transformedResponse = await this.transformer.transform(response, request.transform);

      // 7. Collect metrics
      this.collectMetrics(request, transformedResponse, startTime, requestId);

      return {
        success: true,
        data: transformedResponse,
        meta: {
          requestId,
          timestamp: new Date().toISOString(),
          duration: Date.now() - startTime,
          endpoint: request.endpoint
        }
      };

    } catch (error) {
      const errorResponse = this.handleError(error, request, requestId, startTime);

      // Record circuit breaker failure
      const circuitBreaker = this.getCircuitBreaker(request.endpoint);
      circuitBreaker.recordFailure();

      return errorResponse;
    }
  }

  /**
   * Convenient HTTP method wrappers
   */
  async get<T = unknown>(endpoint: string, options?: Partial<GatewayRequest>): Promise<GatewayResponse<T>> {
    return this.request<never, T>({ method: 'GET', endpoint, ...options });
  }

  async post<TRequest = unknown, TResponse = unknown>(
    endpoint: string,
    data: TRequest,
    options?: Partial<GatewayRequest>
  ): Promise<GatewayResponse<TResponse>> {
    return this.request<TRequest, TResponse>({ method: 'POST', endpoint, data, ...options });
  }

  async put<TRequest = unknown, TResponse = unknown>(
    endpoint: string,
    data: TRequest,
    options?: Partial<GatewayRequest>
  ): Promise<GatewayResponse<TResponse>> {
    return this.request<TRequest, TResponse>({ method: 'PUT', endpoint, data, ...options });
  }

  async delete<T = unknown>(endpoint: string, options?: Partial<GatewayRequest>): Promise<GatewayResponse<T>> {
    return this.request<never, T>({ method: 'DELETE', endpoint, ...options });
  }

  /**
   * Request validation middleware
   */
  private async validateRequest(request: GatewayRequest, requestId: string): Promise<void> {
    const validation = await this.validator.validate(request);

    if (!validation.valid) {
      logger.warn('Request validation failed', {
        requestId,
        endpoint: request.endpoint,
        errors: validation.errors
      });
      throw new Error(`Validation failed: ${validation.errors.join(', ')}`);
    }
  }

  /**
   * Authentication middleware
   */
  private async authenticateRequest(request: GatewayRequest, requestId: string): Promise<void> {
    // Skip auth for whitelisted routes
    if (this.config.auth.skipRoutes.includes(request.endpoint)) {
      return;
    }

    // Check CSRF token for state-changing operations
    if (['POST', 'PUT', 'DELETE', 'PATCH'].includes(request.method)) {
      const csrfToken = request.headers?.['X-CSRF-Token'];
      if (!csrfToken) {
        logger.warn('Missing CSRF token', { requestId, endpoint: request.endpoint });
        throw new Error('CSRF token required');
      }

      // In a full implementation, validate CSRF token here
      // For now, just check presence
    }

    // Additional auth checks would go here (JWT validation, etc.)
    logger.debug('Request authenticated', { requestId, endpoint: request.endpoint });
  }

  /**
   * Rate limiting middleware
   */
  private async checkRateLimit(request: GatewayRequest, requestId: string): Promise<void> {
    const identifier = this.getClientIdentifier(request);
    const allowed = await this.rateLimiter.isAllowed(identifier, request.endpoint);

    if (!allowed) {
      logger.warn('Rate limit exceeded', {
        requestId,
        endpoint: request.endpoint,
        identifier
      });
      throw new Error('Rate limit exceeded');
    }
  }

  /**
   * Execute request with retry logic
   */
  private async executeWithRetries<TRequest, TResponse>(
    request: GatewayRequest<TRequest>,
    requestId: string
  ): Promise<TResponse> {
    const maxRetries = request.retries ?? this.config.retries;
    let lastError: Error;

    for (let attempt = 0; attempt <= maxRetries; attempt++) {
      try {
        const response = await this.executeRequest(request, requestId, attempt);

        // Record circuit breaker success
        const circuitBreaker = this.getCircuitBreaker(request.endpoint);
        circuitBreaker.recordSuccess();

        return response;

      } catch (error) {
        lastError = error as Error;

        if (attempt < maxRetries && this.isRetryableError(error)) {
          const delay = this.calculateRetryDelay(attempt);
          logger.info('Retrying request', {
            requestId,
            endpoint: request.endpoint,
            attempt: attempt + 1,
            maxRetries,
            delay
          });
          await this.sleep(delay);
        } else {
          break;
        }
      }
    }

    throw lastError!;
  }

  /**
   * Execute the actual HTTP request
   */
  private async executeRequest<TRequest, TResponse>(
    request: GatewayRequest<TRequest>,
    requestId: string,
    attempt: number
  ): Promise<TResponse> {
    const url = `${this.config.baseUrl}${request.endpoint}`;
    const timeout = request.timeout ?? this.config.timeout;

    // Build request options with security headers
    const requestOptions = withSecurityHeaders(url, {
      method: request.method,
      headers: {
        'Content-Type': 'application/json',
        'X-Request-ID': requestId,
        'X-Retry-Attempt': attempt.toString(),
        ...request.headers
      },
      body: request.data ? JSON.stringify(request.data) : undefined,
      signal: AbortSignal.timeout(timeout),
    });

    logger.debug('Executing HTTP request', {
      requestId,
      method: request.method,
      endpoint: request.endpoint,
      attempt
    });

    const response = await fetch(url, requestOptions);

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`HTTP ${response.status}: ${errorText}`);
    }

    const responseData = await response.json();
    return responseData as TResponse;
  }

  /**
   * Error handling with classification
   */
  private handleError<TRequest>(
    error: unknown,
    request: GatewayRequest<TRequest>,
    requestId: string,
    startTime: number
  ): GatewayResponse<never> {
    const duration = Date.now() - startTime;
    const errorObj = this.normalizeError(error);

    logger.error('Gateway request failed', {
      requestId,
      endpoint: request.endpoint,
      method: request.method,
      duration,
      error: errorObj.message,
      stack: errorObj.stack
    });

    // Collect error metrics
    this.metrics.recordError(request.endpoint, errorObj.code, duration);

    return {
      success: false,
      error: {
        code: errorObj.code,
        message: errorObj.message,
        details: errorObj.details
      },
      meta: {
        requestId,
        timestamp: new Date().toISOString(),
        duration,
        endpoint: request.endpoint
      }
    };
  }

  /**
   * Helper methods
   */
  private getCircuitBreaker(endpoint: string): CircuitBreaker {
    if (!this.circuitBreakers.has(endpoint)) {
      this.circuitBreakers.set(endpoint, new CircuitBreaker({
        failureThreshold: 5,
        recoveryTimeout: 60000,
        monitoringPeriod: 10000
      }));
    }
    return this.circuitBreakers.get(endpoint)!;
  }

  private getClientIdentifier(request: GatewayRequest): string {
    // In production, use actual client identification
    return request.headers?.['X-Client-ID'] || 'anonymous';
  }

  private isRetryableError(error: unknown): boolean {
    if (error instanceof Error) {
      // Retry on network errors, timeouts, and 5xx responses
      return error.message.includes('timeout') ||
             error.message.includes('network') ||
             error.message.includes('HTTP 5');
    }
    return false;
  }

  private calculateRetryDelay(attempt: number): number {
    // Exponential backoff with jitter
    const baseDelay = 1000;
    const maxDelay = 10000;
    const delay = Math.min(baseDelay * Math.pow(2, attempt), maxDelay);
    const jitter = Math.random() * 0.1 * delay;
    return delay + jitter;
  }

  private normalizeError(error: unknown): {
    code: string;
    message: string;
    details?: unknown;
    stack?: string;
  } {
    if (error instanceof Error) {
      return {
        code: error.name || 'Unknown',
        message: error.message,
        stack: error.stack
      };
    }

    return {
      code: 'Unknown',
      message: String(error)
    };
  }

  private collectMetrics<T>(
    request: GatewayRequest,
    response: T,
    startTime: number,
    requestId: string
  ): void {
    if (!this.config.monitoring.enabled) return;

    const duration = Date.now() - startTime;
    this.metrics.recordRequest(request.endpoint, request.method, duration, true);

    if (Math.random() < this.config.monitoring.sampleRate) {
      logger.info('Request completed', {
        requestId,
        endpoint: request.endpoint,
        method: request.method,
        duration,
        responseSize: JSON.stringify(response).length
      });
    }
  }

  private sleep(ms: number): Promise<void> {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  /**
   * Health check endpoint
   */
  async healthCheck(): Promise<{ status: 'healthy' | 'degraded' | 'unhealthy'; details: Record<string, unknown> }> {
    const checks = await Promise.allSettled([
      this.checkDatabaseConnection(),
      this.checkExternalServices(),
      this.checkCircuitBreakers()
    ]);

    const results = checks.map((check, index) => ({
      name: ['database', 'external_services', 'circuit_breakers'][index],
      status: check.status === 'fulfilled' ? 'healthy' : 'unhealthy',
      details: check.status === 'fulfilled' ? check.value : check.reason
    }));

    const healthyCount = results.filter(r => r.status === 'healthy').length;
    const overallStatus = healthyCount === results.length ? 'healthy' :
                         healthyCount > 0 ? 'degraded' : 'unhealthy';

    return {
      status: overallStatus,
      details: Object.fromEntries(results.map(r => [r.name, r]))
    };
  }

  private async checkDatabaseConnection(): Promise<{ connected: boolean }> {
    // Implement actual database health check
    return { connected: true };
  }

  private async checkExternalServices(): Promise<Record<string, boolean>> {
    // Check external service health
    return { vapi: true, twilio: true, maps: true };
  }

  private async checkCircuitBreakers(): Promise<Record<string, string>> {
    const status: Record<string, string> = {};
    for (const [endpoint, breaker] of this.circuitBreakers) {
      status[endpoint] = breaker.getState();
    }
    return status;
  }
}

/**
 * Request Validator - Multi-layer validation pipeline
 * Implements comprehensive validation for API requests
 */

/* eslint-disable @typescript-eslint/no-explicit-any */

import { z } from 'zod';
import { logger } from '@/lib/logger';

export interface ValidationResult {
  valid: boolean;
  errors: string[];
  sanitized?: unknown;
}

export interface ValidationRule {
  name: string;
  validate: (request: unknown) => Promise<ValidationResult> | ValidationResult;
}

export class RequestValidator {
  private rules: ValidationRule[] = [];

  constructor() {
    this.initializeDefaultRules();
  }

  /**
   * Validate a request through all registered rules
   */
  async validate(request: unknown): Promise<ValidationResult> {
    const allErrors: string[] = [];
    let sanitizedRequest = request;

    for (const rule of this.rules) {
      try {
        const result = await rule.validate(sanitizedRequest);

        if (!result.valid) {
          allErrors.push(...result.errors.map(error => `${rule.name}: ${error}`));
        } else if (result.sanitized !== undefined) {
          sanitizedRequest = result.sanitized;
        }
      } catch (error) {
        logger.error('Validation rule failed', {
          rule: rule.name,
          error: error instanceof Error ? error.message : String(error)
        });
        allErrors.push(`${rule.name}: Validation rule error`);
      }
    }

    return {
      valid: allErrors.length === 0,
      errors: allErrors,
      sanitized: sanitizedRequest
    };
  }

  /**
   * Add a custom validation rule
   */
  addRule(rule: ValidationRule): void {
    this.rules.push(rule);
  }

  /**
   * Remove a validation rule by name
   */
  removeRule(name: string): void {
    this.rules = this.rules.filter(rule => rule.name !== name);
  }

  /**
   * Initialize default validation rules
   */
  private initializeDefaultRules(): void {
    // Basic structure validation
    this.addRule({
      name: 'structure',
      validate: (request) => this.validateStructure(request)
    });

    // Input sanitization
    this.addRule({
      name: 'sanitization',
      validate: (request) => this.sanitizeInput(request)
    });

    // Size limits
    this.addRule({
      name: 'size',
      validate: (request) => this.validateSize(request)
    });

    // Security patterns
    this.addRule({
      name: 'security',
      validate: (request) => this.validateSecurity(request)
    });
  }

  /**
   * Validate basic request structure
   */
  private validateStructure(request: unknown): ValidationResult {
    const requestSchema = z.object({
      method: z.enum(['GET', 'POST', 'PUT', 'DELETE', 'PATCH']),
      endpoint: z.string().min(1).regex(/^\/[a-zA-Z0-9/_-]*$/, 'Invalid endpoint format'),
      data: z.unknown().optional(),
      headers: z.record(z.string()).optional(),
      timeout: z.number().positive().max(300000).optional(), // Max 5 minutes
      retries: z.number().int().min(0).max(5).optional(),
      skipAuth: z.boolean().optional(),
      skipRateLimit: z.boolean().optional(),
      transform: z.function().optional()
    });

    try {
      const validated = requestSchema.parse(request);
      return {
        valid: true,
        errors: [],
        sanitized: validated
      };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`)
        };
      }

      return {
        valid: false,
        errors: ['Invalid request structure']
      };
    }
  }

  /**
   * Sanitize input data to prevent injection attacks
   */
  private sanitizeInput(request: unknown): ValidationResult {
    try {
      const sanitized = this.deepSanitize(request);
      return {
        valid: true,
        errors: [],
        sanitized
      };
    } catch (error) {
      return {
        valid: false,
        errors: ['Input sanitization failed']
      };
    }
  }

  /**
   * Validate request size limits
   */
  private validateSize(request: unknown): ValidationResult {
    const errors: string[] = [];

    try {
      const requestString = JSON.stringify(request);
      const sizeInBytes = new Blob([requestString]).size;

      // 10MB limit for requests
      const maxSize = 10 * 1024 * 1024;
      if (sizeInBytes > maxSize) {
        errors.push(`Request size ${sizeInBytes} exceeds limit of ${maxSize} bytes`);
      }

      // Check data field specifically
      const req = request as any;
      if (req?.data) {
        const dataString = JSON.stringify(req.data);
        const dataSizeInBytes = new Blob([dataString]).size;

        // 5MB limit for data payload
        const maxDataSize = 5 * 1024 * 1024;
        if (dataSizeInBytes > maxDataSize) {
          errors.push(`Data payload size ${dataSizeInBytes} exceeds limit of ${maxDataSize} bytes`);
        }
      }

    } catch (error) {
      errors.push('Unable to calculate request size');
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }

  /**
   * Validate against security patterns
   */
  private validateSecurity(request: unknown): ValidationResult {
    const errors: string[] = [];
    const req = request as any;

    // Check for dangerous patterns in strings
    const dangerousPatterns = [
      /<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, // Script tags
      /javascript:/gi,                                        // JavaScript URLs
      /on\w+\s*=/gi,                                         // Event handlers
      /expression\s*\(/gi,                                   // CSS expressions
      /eval\s*\(/gi,                                         // Eval calls
      /setTimeout\s*\(/gi,                                   // setTimeout calls
      /setInterval\s*\(/gi,                                  // setInterval calls
    ];

    const checkForPatterns = (obj: unknown, path = ''): void => {
      if (typeof obj === 'string') {
        for (const pattern of dangerousPatterns) {
          if (pattern.test(obj)) {
            errors.push(`Potentially dangerous content detected at ${path || 'root'}`);
            break;
          }
        }
      } else if (Array.isArray(obj)) {
        obj.forEach((item, index) => {
          checkForPatterns(item, `${path}[${index}]`);
        });
      } else if (obj && typeof obj === 'object') {
        Object.entries(obj).forEach(([key, value]) => {
          const newPath = path ? `${path}.${key}` : key;
          checkForPatterns(value, newPath);
        });
      }
    };

    checkForPatterns(req);

    // Check endpoint for path traversal
    if (req?.endpoint && typeof req.endpoint === 'string') {
      if (req.endpoint.includes('..') || req.endpoint.includes('~')) {
        errors.push('Path traversal attempt detected in endpoint');
      }
    }

    // Check headers for dangerous values
    if (req?.headers && typeof req.headers === 'object') {
      for (const [key, value] of Object.entries(req.headers)) {
        if (typeof value === 'string') {
          // Check for header injection
          if (value.includes('\n') || value.includes('\r')) {
            errors.push(`Header injection attempt detected in ${key}`);
          }
        }
      }
    }

    return {
      valid: errors.length === 0,
      errors
    };
  }

  /**
   * Deep sanitize an object, removing dangerous content
   */
  private deepSanitize(obj: unknown): unknown {
    if (typeof obj === 'string') {
      return this.sanitizeString(obj);
    }

    if (Array.isArray(obj)) {
      return obj.map(item => this.deepSanitize(item));
    }

    if (obj && typeof obj === 'object') {
      const sanitized: Record<string, unknown> = {};
      for (const [key, value] of Object.entries(obj)) {
        // Sanitize key names
        const sanitizedKey = this.sanitizeString(key);
        sanitized[sanitizedKey] = this.deepSanitize(value);
      }
      return sanitized;
    }

    return obj;
  }

  /**
   * Sanitize a string value
   */
  private sanitizeString(str: string): string {
    return str
      // Remove null bytes
      // eslint-disable-next-line no-control-regex
      .replace(/\x00/g, '')
      // Remove control characters except newlines and tabs
      // eslint-disable-next-line no-control-regex
      .replace(/[\x01-\x08\x0B\x0C\x0E-\x1F\x7F]/g, '')
      // Normalize whitespace
      .replace(/\s+/g, ' ')
      // Trim
      .trim();
  }

  /**
   * Validate specific data schemas based on endpoint
   */
  validateEndpointData(endpoint: string, data: unknown): ValidationResult {
    // Define endpoint-specific schemas
    const endpointSchemas: Record<string, z.ZodSchema> = {
      '/api/calls': z.object({
        phoneNumber: z.string().regex(/^\+?[1-9]\d{1,14}$/, 'Invalid phone number'),
        assistantId: z.string().uuid().optional(),
        context: z.record(z.unknown()).optional()
      }),

      '/api/sms': z.object({
        to: z.string().regex(/^\+?[1-9]\d{1,14}$/, 'Invalid phone number'),
        message: z.string().min(1).max(1600, 'Message too long'),
        priority: z.enum(['low', 'normal', 'high']).optional()
      }),

      '/api/support': z.object({
        type: z.enum(['call', 'chat', 'email', 'sms']),
        message: z.string().min(1).max(5000),
        urgency: z.enum(['low', 'normal', 'high']).optional(),
        clientInfo: z.object({
          name: z.string().optional(),
          phone: z.string().optional(),
          email: z.string().email().optional()
        }).optional()
      })
    };

    const schema = endpointSchemas[endpoint];
    if (!schema) {
      return { valid: true, errors: [] }; // No specific validation
    }

    try {
      const validated = schema.parse(data);
      return {
        valid: true,
        errors: [],
        sanitized: validated
      };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`)
        };
      }

      return {
        valid: false,
        errors: ['Data validation failed']
      };
    }
  }
}

/* eslint-disable @typescript-eslint/no-explicit-any, @typescript-eslint/no-unused-vars */
/**
 * Response Transformer - Handles response transformation and formatting
 * Provides consistent response structure and data transformation
 */

import { logger } from '@/lib/logger';

export interface TransformConfig {
  maskSensitiveData?: boolean;
  normalizeStructure?: boolean;
  addMetadata?: boolean;
}

export class ResponseTransformer {
  private sensitiveFields = [
    'password', 'token', 'secret', 'key', 'authorization',
    'ssn', 'social_security', 'credit_card', 'bank_account'
  ];

  /**
   * Transform response data with optional custom transformer
   */
  async transform<T>(
    data: T,
    customTransform?: (data: T) => unknown,
    config: TransformConfig = {}
  ): Promise<T> {
    try {
      let transformed = data;

      // Apply custom transformation first
      if (customTransform) {
        transformed = customTransform(transformed) as T;
      }

      // Apply built-in transformations
      if (config.maskSensitiveData !== false) {
        transformed = this.maskSensitiveData(transformed);
      }

      if (config.normalizeStructure) {
        transformed = this.normalizeStructure(transformed);
      }

      return transformed;

    } catch (error) {
      logger.error('Response transformation failed', {
        error: error instanceof Error ? error.message : String(error)
      });
      return data; // Return original data if transformation fails
    }
  }

  /**
   * Mask sensitive data in response
   */
  private maskSensitiveData<T>(data: T): T {
    if (data === null || data === undefined) {
      return data;
    }

    if (Array.isArray(data)) {
      return data.map(item => this.maskSensitiveData(item)) as T;
    }

    if (typeof data === 'object') {
      const masked = { ...data } as Record<string, unknown>;

      for (const [key, value] of Object.entries(masked)) {
        const lowerKey = key.toLowerCase();

        // Check if field should be masked
        if (this.sensitiveFields.some(field => lowerKey.includes(field))) {
          if (typeof value === 'string' && value.length > 0) {
            // Show first 2 and last 2 characters for identifiable data
            if (value.length > 6) {
              masked[key] = `${value.substring(0, 2)}***${value.substring(value.length - 2)}`;
            } else {
              masked[key] = '***';
            }
          } else {
            masked[key] = '***';
          }
        } else {
          // Recursively mask nested objects
          masked[key] = this.maskSensitiveData(value);
        }
      }

      return masked as T;
    }

    return data;
  }

  /**
   * Normalize response structure for consistency
   */
  private normalizeStructure<T>(data: T): T {
    if (data === null || data === undefined) {
      return data;
    }

    if (Array.isArray(data)) {
      return data.map(item => this.normalizeStructure(item)) as T;
    }

    if (typeof data === 'object') {
      const normalized = { ...data } as Record<string, unknown>;

      // Convert timestamp strings to ISO format
      for (const [key, value] of Object.entries(normalized)) {
        if (typeof value === 'string' && this.isTimestamp(key, value)) {
          try {
            const date = new Date(value);
            if (!isNaN(date.getTime())) {
              normalized[key] = date.toISOString();
            }
          } catch {
            // Keep original value if parsing fails
          }
        } else if (typeof value === 'object') {
          normalized[key] = this.normalizeStructure(value);
        }
      }

      // Ensure consistent naming conventions
      const standardized = this.standardizeFieldNames(normalized);

      return standardized as T;
    }

    return data;
  }

  /**
   * Check if a field value looks like a timestamp
   */
  private isTimestamp(key: string, value: string): boolean {
    const timestampFields = ['created_at', 'updated_at', 'timestamp', 'date', 'time'];
    const lowerKey = key.toLowerCase();

    if (timestampFields.some(field => lowerKey.includes(field))) {
      // Check if value looks like a date
      return /^\d{4}-\d{2}-\d{2}/.test(value) ||
             /^\d{10,13}$/.test(value) || // Unix timestamp
             !isNaN(Date.parse(value));
    }

    return false;
  }

  /**
   * Standardize field names to consistent format
   */
  private standardizeFieldNames(obj: Record<string, unknown>): Record<string, unknown> {
    const standardized: Record<string, unknown> = {};
    const fieldMappings: Record<string, string> = {
      'id': 'id',
      'ID': 'id',
      'userId': 'user_id',
      'callId': 'call_id',
      'phoneNumber': 'phone_number',
      'createdAt': 'created_at',
      'updatedAt': 'updated_at',
      'startedAt': 'started_at',
      'endedAt': 'ended_at',
      'customerName': 'customer_name',
      'customerPhone': 'customer_phone',
      'customerEmail': 'customer_email'
    };

    for (const [key, value] of Object.entries(obj)) {
      const standardKey = fieldMappings[key] || this.camelToSnake(key);
      standardized[standardKey] = value;
    }

    return standardized;
  }

  /**
   * Convert camelCase to snake_case
   */
  private camelToSnake(str: string): string {
    return str.replace(/[A-Z]/g, letter => `_${letter.toLowerCase()}`);
  }

  /**
   * Transform error responses to consistent format
   */
  transformError(error: unknown): {
    code: string;
    message: string;
    details?: unknown;
    timestamp: string;
  } {
    if (error instanceof Error) {
      return {
        code: error.name || 'UnknownError',
        message: error.message,
        details: this.extractErrorDetails(error),
        timestamp: new Date().toISOString()
      };
    }

    if (typeof error === 'object' && error !== null) {
      const errorObj = error as Record<string, unknown>;
      return {
        code: String(errorObj.code || errorObj.name || 'UnknownError'),
        message: String(errorObj.message || 'An error occurred'),
        details: errorObj.details || errorObj,
        timestamp: new Date().toISOString()
      };
    }

    return {
      code: 'UnknownError',
      message: String(error),
      timestamp: new Date().toISOString()
    };
  }

  /**
   * Extract additional error details while masking sensitive info
   */
  private extractErrorDetails(error: Error): Record<string, unknown> {
    const details: Record<string, unknown> = {};

    // Add stack trace in development
    if (process.env.NODE_ENV === 'development' && error.stack) {
      details.stack = error.stack;
    }

    // Add any additional error properties
    const errorProps = Object.getOwnPropertyNames(error);
    for (const prop of errorProps) {
      if (prop !== 'name' && prop !== 'message' && prop !== 'stack') {
        try {
          const value = (error as any)[prop];
          details[prop] = this.maskSensitiveData(value);
        } catch {
          // Skip properties that can't be accessed
        }
      }
    }

    return details;
  }

  /**
   * Transform paginated responses to consistent format
   */
  transformPaginatedResponse<T>(data: {
    items: T[];
    total?: number;
    count?: number;
    page?: number;
    pageSize?: number;
    hasMore?: boolean;
  }): {
    items: T[];
    pagination: {
      total: number;
      count: number;
      page: number;
      pageSize: number;
      hasMore: boolean;
      totalPages: number;
    };
  } {
    const items = data.items || [];
    const total = data.total ?? data.count ?? items.length;
    const page = data.page ?? 1;
    const pageSize = data.pageSize ?? items.length || 25;
    const totalPages = Math.ceil(total / pageSize);
    const hasMore = data.hasMore ?? (page < totalPages);

    return {
      items: items.map(item => this.maskSensitiveData(item)),
      pagination: {
        total,
        count: items.length,
        page,
        pageSize,
        hasMore,
        totalPages
      }
    };
  }

  /**
   * Add performance metadata to response
   */
  addPerformanceMetadata<T>(
    data: T,
    startTime: number,
    additionalMeta?: Record<string, unknown>
  ): T & { _meta: Record<string, unknown> } {
    const duration = Date.now() - startTime;

    return {
      ...data as object,
      _meta: {
        processingTime: duration,
        timestamp: new Date().toISOString(),
        ...additionalMeta
      }
    } as T & { _meta: Record<string, unknown> };
  }
}

/**
 * Client API typ√© pour le frontend (compatible Next.js/Vite)
 * - Base URL: NEXT_PUBLIC_API_BASE_URL ou VITE_API_BASE_URL
 * - Masquage PII dans les logs d'erreurs
 * - Gestion d'erreurs uniforme
 * - CSRF protection int√©gr√©e
 */
import { logger } from '@/lib/logger';
import { csrfProtection } from './security/csrf';
import { withSecurityHeaders } from './security/headers';

interface ImportMetaEnv {
  NEXT_PUBLIC_API_BASE_URL?: string;
  VITE_API_BASE_URL?: string;
}

interface ProcessEnv {
  NEXT_PUBLIC_API_BASE_URL?: string;
  VITE_API_BASE_URL?: string;
}

export function getApiBaseUrl(): string {
  const env = ((import.meta as { env?: ImportMetaEnv })?.env) || {};
  const penv = ((globalThis as { process?: { env?: ProcessEnv } })?.process?.env) || {};
  const fromImport = env.NEXT_PUBLIC_API_BASE_URL || env.VITE_API_BASE_URL;
  const fromProcess = penv.NEXT_PUBLIC_API_BASE_URL || penv.VITE_API_BASE_URL;
  const fromGlobal = (globalThis as { NEXT_PUBLIC_API_BASE_URL?: string; VITE_API_BASE_URL?: string }).NEXT_PUBLIC_API_BASE_URL ||
                     (globalThis as { NEXT_PUBLIC_API_BASE_URL?: string; VITE_API_BASE_URL?: string }).VITE_API_BASE_URL;
  return String(fromImport || fromProcess || fromGlobal || 'http://localhost:8080');
}

const API_BASE_URL = getApiBaseUrl();

function maskPII(input: unknown): unknown {
  try {
    if (!input) return input;
    const s = typeof input === 'string' ? input : JSON.stringify(input);
    let out = s.replace(/([A-Z0-9._%+-])[A-Z0-9._%+-]*(@[A-Z0-9.-]+\.[A-Z]{2,})/gi, '$1***$2');
    out = out.replace(/(\+?\d{1,3}[\s.-]?)?(\d{3})[\s.-]?(\d{3})[\s.-]?(\d{4})/g, (_m, _c, a) => `${a}-***-****`);
    return typeof input === 'string' ? out : JSON.parse(out);
  } catch {
    return input;
  }
}

export async function apiFetch<T>(endpoint: string, options?: RequestInit): Promise<T> {
  const url = `${API_BASE_URL}${endpoint}`;

  // Get CSRF token
  const csrfToken = csrfProtection.getTokenFromMeta() || csrfProtection.getTokenFromCookie();

  // Merge security headers with request
  const secureOptions = withSecurityHeaders(url, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      'X-CSRF-Token': csrfToken || '',
      ...(options?.headers || {})
    },
    credentials: 'include',
  });

  try {
    const res = await fetch(url, secureOptions);
    if (!res.ok) {
      const text = await res.text();
      logger.warn('[API] R√©ponse non OK', { url, status: res.status });
      throw new Error(`${res.status} ${res.statusText}: ${text}`);
    }
    return (await res.json()) as T;
  } catch (error) {
    logger.error('[API] √âchec requ√™te', { url, error: maskPII(error as any) });
    throw error;
  }
}

// Pagination g√©n√©rique
export interface Paginated<T> {
  items: T[];
  total: number;
  page: number;
  pageSize: number;
}

export const apiClient = {
  get constraints() {
    return {
      list: async (params?: { page?: number; pageSize?: number; search?: string; category?: string; priority?: 'P1'|'P2'|'P3'|'P4'; sort?: string; }) => {
        const q = new URLSearchParams();
        if (params?.page) q.set('page', String(params.page));
        if (params?.pageSize) q.set('pageSize', String(params.pageSize));
        if (params?.search) q.set('search', params.search);
        if (params?.category) q.set('category', params.category);
        if (params?.priority) q.set('priority', params.priority);
        if (params?.sort) q.set('sort', params.sort);
        const endpoint = `/api/constraints${q.toString() ? `?${q}` : ''}`;
        return apiFetch<any>(endpoint);
      }
    };
  }
};

export default apiClient;

import { supabaseServices } from '@/services/supabaseServices';
import { logger } from '@/lib/logger';
import { supabase } from '@/integrations/supabase/client';
import type {
  CallData,
  LeadData,
  AnalyticsData,
  SettingsData,
  ConnectionTestResult,
  APIResponse,
  APIError
} from '@/types/api';

// Configuration de l'API
const API_BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:8080';

// Helper pour les requ√™tes avec gestion d'erreur
async function fetchApi<T>(endpoint: string, options?: RequestInit): Promise<T> {
  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers: {
        'Content-Type': 'application/json',
        ...options?.headers,
      },
      credentials: 'include',
    });

    if (!response.ok) {
      throw new Error(`API Error: ${response.status} ${response.statusText}`);
    }

    return await response.json() as T;
  } catch (error) {
    logger.error(`Error fetching ${endpoint}:`, error);
    throw error;
  }
}

// API Services qui combinent backend local et Supabase
export const api = {
  // Appels
  async getCalls(): Promise<CallData[]> {
    try {
      // Essayer d'abord le backend local
      return await fetchApi<CallData[]>('/api/calls');
    } catch {
      // Fallback sur Supabase
      const calls = await supabaseServices.getRecentCalls(50);
      return calls.map(call => ({
        id: call.id,
        phoneNumber: call.phone_number,
        startTime: new Date(call.started_at),
        endTime: call.ended_at ? new Date(call.ended_at) : undefined,
        duration: call.duration || 0,
        transcript: call.transcript || '',
        priority: (call.priority as CallData['priority']) || 'P4',
        status: (call.status as CallData['status']) || 'completed',
        recordingUrl: call.recording_url,
        metadata: (call.metadata as CallData['metadata']) || {},
      }));
    }
  },

  async getCall(id: string): Promise<CallData | null> {
    try {
      return await fetchApi<CallData>(`/api/calls/${id}`);
    } catch {
      const { data } = await supabase
        .from('vapi_calls')
        .select('*')
        .eq('id', id)
        .single();

      if (!data) return null;

      return {
        id: data.id,
        phoneNumber: data.phone_number,
        startTime: new Date(data.started_at),
        endTime: data.ended_at ? new Date(data.ended_at) : undefined,
        duration: data.duration || 0,
        transcript: data.transcript || '',
        priority: (data.priority as CallData['priority']) || 'P4',
        status: (data.status as CallData['status']) || 'completed',
        recordingUrl: data.recording_url,
        metadata: (data.metadata as CallData['metadata']) || {},
      };
    }
  },

  // Leads
  async getLeads(): Promise<LeadData[]> {
    try {
      return await fetchApi<LeadData[]>('/api/leads');
    } catch {
      const leads = await supabaseServices.getLeads();
      return leads.map(lead => ({
        id: lead.id,
        email: lead.email,
        phone: lead.phone,
        name: lead.name,
        status: (lead.status as LeadData['status']) || 'new',
        source: lead.source,
        created_at: lead.created_at,
        updated_at: lead.updated_at,
        metadata: (lead.metadata as LeadData['metadata']) || {},
      }));
    }
  },

  async createLead(lead: Partial<LeadData>): Promise<LeadData> {
    try {
      return await fetchApi<LeadData>('/api/leads', {
        method: 'POST',
        body: JSON.stringify(lead),
      });
    } catch {
      const { data, error } = await supabase
        .from('leads')
        .insert(lead)
        .select()
        .single();
      if (error) throw error;

      return {
        id: data.id,
        email: data.email,
        phone: data.phone,
        name: data.name,
        status: (data.status as LeadData['status']) || 'new',
        source: data.source,
        created_at: data.created_at,
        updated_at: data.updated_at,
        metadata: (data.metadata as LeadData['metadata']) || {},
      };
    }
  },

  // Analytics
  async getAnalytics(): Promise<AnalyticsData> {
    try {
      return await fetchApi<AnalyticsData>('/api/analytics');
    } catch {
      // Utiliser les m√©triques de Supabase
      const metrics = await supabaseServices.getDashboardMetrics();
      return {
        totalCalls: metrics.totalCalls,
        todayCalls: metrics.todayCalls,
        totalLeads: metrics.totalLeads,
        conversionRate: metrics.conversionRate,
        metrics: [
          { name: 'Calls', value: metrics.totalCalls, trend: 'stable' },
          { name: 'Leads', value: metrics.totalLeads, trend: 'stable' },
        ],
        timeRange: {
          start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000), // 30 days ago
          end: new Date(),
          period: 'month'
        }
      };
    }
  },

  // Configuration & Settings
  async getSettings(): Promise<SettingsData> {
    try {
      return await fetchApi<SettingsData>('/api/settings');
    } catch {
      // Retourner des settings par d√©faut
      return {
        constraints: [],
        pricing: {
          baseRate: 0,
          minimumCharge: 0,
          currency: 'USD'
        },
        prompts: {
          greeting: 'Hello!',
          farewell: 'Goodbye!',
          errorMessage: 'An error occurred'
        },
        webhooks: {
          url: import.meta.env.VITE_VAPI_WEBHOOK_URL || '',
          events: [],
          timeout: 5000
        }
      };
    }
  },

  async updateSettings(settings: Partial<SettingsData>): Promise<SettingsData> {
    try {
      return await fetchApi<SettingsData>('/api/settings', {
        method: 'PUT',
        body: JSON.stringify(settings),
      });
    } catch (error) {
      logger.error('Error updating settings:', error);
      throw error;
    }
  },

  // Test de connexion
  async testConnection(): Promise<ConnectionTestResult> {
    const results: ConnectionTestResult = {
      backend: false,
      supabase: false,
      timestamp: new Date(),
      details: {}
    };

    // Test backend local
    try {
      const startTime = Date.now();
      await fetch(`${API_BASE_URL}/health`);
      results.backend = true;
      results.details.backendUrl = API_BASE_URL;
      results.details.latency = Date.now() - startTime;
    } catch (error) {
      results.backend = false;
      results.details.error = error instanceof Error ? error.message : 'Unknown error';
    }

    // Test Supabase
    try {
      const { error } = await supabase.from('vapi_calls').select('count').limit(1);
      results.supabase = !error;
      if (error) {
        results.details.error = error.message;
      }
    } catch (error) {
      results.supabase = false;
      results.details.error = error instanceof Error ? error.message : 'Unknown error';
    }

    return results;
  },
};

