import { serve } from "https://deno.land/std@0.224.0/http/server.ts";
import { corsHeaders, handleCors } from '../_shared/cors.ts';
import { createServiceClient } from '../_shared/supabase.ts';

interface SupportRequest {
  client_name: string;
  client_phone?: string;
  client_email?: string;
  subject: string;
  description: string;
  category?: 'general' | 'technical' | 'billing' | 'urgent' | 'feedback';
  priority?: 'low' | 'normal' | 'high' | 'urgent';
}

serve(async (req) => {
  // Handle CORS preflight requests
  const corsResponse = handleCors(req);
  if (corsResponse) return corsResponse;

  try {
    const supabase = createServiceClient();

    if (req.method !== 'POST') {
      throw new Error('Method not allowed');
    }

    const supportRequest: SupportRequest = await req.json();

    // Validation des donn√©es obligatoires
    if (!supportRequest.client_name || !supportRequest.subject || !supportRequest.description) {
      throw new Error('client_name, subject et description sont obligatoires');
    }

    // Cr√©er le ticket de support dans la base de donn√©es
    const { data: ticket, error: ticketError } = await supabase
      .from('support_tickets')
      .insert({
        client_name: supportRequest.client_name,
        client_phone: supportRequest.client_phone,
        client_email: supportRequest.client_email,
        subject: supportRequest.subject,
        description: supportRequest.description,
        category: supportRequest.category || 'general',
        priority: supportRequest.priority || 'normal',
        status: 'open'
      })
      .select()
      .single();

    if (ticketError) {
      console.error('Erreur cr√©ation ticket:', ticketError);
      throw new Error(`Erreur cr√©ation ticket: ${ticketError.message}`);
    }

    console.log('Ticket de support cr√©√©:', ticket);

    // Logger l'action dans audit_logs
    const { error: auditError } = await supabase
      .from('audit_logs')
      .insert({
        user_email: supportRequest.client_email || 'anonymous',
        action: 'CREATE',
        resource_type: 'support_tickets',
        resource_id: ticket.id,
        new_values: ticket,
        metadata: {
          source: 'support-feedback-function',
          client_name: supportRequest.client_name
        }
      });

    if (auditError) {
      console.warn('Erreur audit log:', auditError);
    }

    // Si le ticket est urgent, cr√©er une alerte
    if (supportRequest.priority === 'urgent') {
      const { error: alertError } = await supabase
        .from('alerts')
        .insert({
          type: 'support_urgent',
          message: `Ticket urgent cr√©√©: ${supportRequest.subject}`,
          priority: 'high',
          metadata: {
            ticket_id: ticket.id,
            client_name: supportRequest.client_name,
            client_phone: supportRequest.client_phone
          }
        });

      if (alertError) {
        console.warn('Erreur cr√©ation alerte:', alertError);
      }
    }

    return new Response(
      JSON.stringify({
        success: true,
        ticket,
        message: 'Ticket de support cr√©√© avec succ√®s'
      }),
      {
        status: 201,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );

  } catch (error) {
    console.error('Erreur support-feedback:', error);

    return new Response(
      JSON.stringify({
        success: false,
        error: error instanceof Error ? error.message : 'Erreur interne du serveur'
      }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' }
      }
    );
  }
});

import { serve } from "https://deno.land/std@0.224.0/http/server.ts";
import { corsHeaders, handleCors } from '../_shared/cors.ts';
import { createServiceClient } from '../_shared/supabase.ts';

serve(async (req) => {
  // Handle CORS preflight requests
  const corsResponse = handleCors(req);
  if (corsResponse) return corsResponse;

  const start = Date.now();
  try {
    if (req.method !== 'POST') {
      return new Response(JSON.stringify({ success: false, error: 'Method not allowed' }), { status: 405, headers: { ...corsHeaders, 'Content-Type': 'application/json' } });
    }

    const { phone_number, assistant_id, context } = await req.json();

    const supabase = createServiceClient();

    // If VAPI credentials exist, call external API, otherwise simulate
    const vapiKey = Deno.env.get('VAPI_API_KEY');
    let external:
      | { called: true; status: number; id?: string }
      | { called: false; reason: string } = { called: false, reason: 'no_credentials' };

    if (vapiKey) {
      try {
        // Placeholder: example external call - adjust path if needed
        const resp = await fetch('https://api.vapi.ai/v1/calls', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${vapiKey}` },
          body: JSON.stringify({ phoneNumber: phone_number, assistantId: assistant_id, context }),
        });
        const data = await resp.json();
        external = { called: true, status: resp.status, id: data?.id };
      } catch (_) {
        external = { called: false, reason: 'external_error' };
      }
    }

    // Log call in DB (simulate if needed)
    const { data: row, error } = await supabase
      .from('vapi_calls')
      .insert({
        call_id: crypto.randomUUID(),
        phone_number,
        customer_name: context?.client_name ?? null,
        priority: context?.urgency ?? null,
        status: 'pending',
        metadata: { context, external },
      })
      .select()
      .single();

    if (error) {
      throw error;
    }

    const latency = Date.now() - start;
    console.log(JSON.stringify({ service: 'vapi-call', status: 'ok', latency_ms: latency }));

    return new Response(
      JSON.stringify({ success: true, data: row, metrics: { p50_ms: latency, p95_ms: latency, p99_ms: latency } }),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (e) {
    const latency = Date.now() - start;
    console.error(JSON.stringify({ service: 'vapi-call', status: 'error', error: String(e), latency_ms: latency }));
    return new Response(JSON.stringify({ success: false, error: 'Internal error' }), { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } });
  }
});
